style.css:
@-moz-document domain("chat.openai.com") {
.text-gray-800,
    .text-gray-100,
    .text-white,
    button,
    input,
    optgroup,
    select,
    textarea {
      color: white;
      font-family: monaco, 'monospace';
    }

    .mb-2,
    .mb-4,
    .dark .dark\:text-gray-100:nth-child(even) p,
    .dark .dark\:text-gray-100:nth-child(even) a {
      color: black;
      font-family: monaco, 'monospace';
    }

    .dark .dark\:bg-gray-800,
    .dark .dark\:text-gray-100:nth-child(even),
    .react-scroll-to-bottom--css-fcsbf-79elbk.h-full.dark\:bg-gray-800,
    .flex.flex-col.items-center.text-sm.dark\:bg-gray-800,
    .react-scroll-to-bottom--css-lwrfy-79elbk.h-full.dark\:bg-gray-800,
    .flex.flex-col.w-full.py-2.flex-grow.md\:py-3.md\:pl-4.relative.border.border-black\/10.bg-white.dark\:border-gray-900\/50.dark\:text-white.dark\:bg-gray-700.rounded-md.shadow-\[0_0_10px_rgba\(0\,0\,0\,0\.10\)\].dark\:shadow-\[0_0_15px_rgba\(0\,0\,0\,0\.10\)\],
    .dark .dark\:bg-gray-800 {
      background: black !important;
      border: none;
    }

    .dark .dark\:text-gray-100:nth-child(odd),
    .w-full.h-32.md\:h-48.flex-shrink-0,
    .dark .dark\:md\:bg-vert-dark-gradient {
      background: black;
      border: none;
      font-family: monaco, 'monospace';
    }

    .scrollbar-trigger.flex.h-full.w-full.flex-1.items-start.border-white\/20 {
      background: black;
      border: none;
      font-family: monaco, 'monospace';
    }

    h1.text-4xl.font-semibold.text-center.mt-6.sm\:mt-\[20vh\].ml-auto.mr-auto.mb-10.sm\:mb-16.flex.gap-2.items-center.justify-center {
      font-size: 0;
    }

    h1.text-4xl.font-semibold.text-center.mt-6.sm\:mt-\[20vh\].ml-auto.mr-auto.mb-10.sm\:mb-16.flex.gap-2.items-center.justify-center::after {
      content: 'Hi, Insert Name...';
      font-size: 20px;
    }

    .flex-1.text-ellipsis.max-h-5.overflow-hidden.break-all.relative {
      font-size: 10px;
    }

    .w-\[30px\].flex.flex-col.relative.items-end,
    .px-3.pt-2.pb-3.text-center.text-xs.text-black\/50.dark\:text-white\/50.md\:px-4.md\:pt-3.md\:pb-6,
    .from-gray-900,
    .border-t.border-white\/20.pt-2,
    .md\:flex.items-start.text-center.gap-3\.5 {
      display: none;
    }

    form.stretch.mx-2.flex.flex-row.gap-3.last\:mb-2.md\:mx-4.md\:last\:mb-6.lg\:mx-auto.lg\:max-w-2xl.xl\:max-w-3xl {
      margin-bottom: 90px;
    }
    .w-full{
        background-color:black;
        color: white;
      font-family: monaco, 'monospace';
    }
    #__next > div.relative.z-0.flex.h-full.w-full.overflow-hidden > div.relative.flex.h-full.max-w-full.flex-1.flex-col.overflow-hidden > main > div.flex.h-full.flex-col > div.w-full.pt-2.md\:pt-0.dark\:border-white\/20.md\:border-transparent.md\:dark\:border-transparent.md\:w-\[calc\(100\%-\.5rem\)\] > div{
       display:none;
    }
    #prompt-textarea::placeholder{
      color: transparent;
    }


    .rounded-sm{
      display:none;
    }

    .flex:nth-child(1) > span:nth-child(1) > .btn {
       /*Your code should go under this comment*/
    }

    button{
      background-color:black !important;
    }

    .dark .dark\:text-black {
        --tw-text-opacity: 0.5;
        color: rgba(255,255,255,var(--tw-text-opacity));
    }

    .dark .dark\:border-white {
        --tw-border-opacity: 0.5;
        border-color: rgba(255,255,255,var(--tw-border-opacity));
    }

    .mr-2{
      border-color: rgba(255,255,255,0.5);
    }


    #__next > div.relative.z-0.flex.h-full.w-full.overflow-hidden > div.relative.flex.h-full.max-w-full.flex-1.flex-col.overflow-hidden > main > div.flex.h-full.flex-col > div.w-full.pt-2.md\:pt-0.dark\:border-white\/20.md\:border-transparent.md\:dark\:border-transparent.md\:w-\[calc\(100\%-\.5rem\)\] > form > div > div.flex.w-full.items-center > div > button{
      color:white !important;
    }
    #__next > div.relative.z-0.flex.h-full.w-full.overflow-hidden > div.relative.flex.h-full.max-w-full.flex-1.flex-col.overflow-hidden > main > div.flex.h-full.flex-col > div.flex-1.overflow-hidden > div > div > div > div.sticky.top-0.mb-1\.5.flex.items-center.justify-between.z-10.h-14.bg-white.p-2.font-semibold.dark\:bg-gray-800 > div.flex.items-center.gap-2 > div{
      display:none;
    }

    .dark .dark\:from-gray-700 {
        --tw-gradient-from: #38383800 var(--tw-gradient-from-position);
        --tw-gradient-to: rgba(64,65,79,0) var(--tw-gradient-to-position);
        --tw-gradient-stops: var(--tw-gradient-from),var(--tw-gradient-to);
      }
}
