style.css:
@-moz-document url("https://calc.pokemonshowdown.com/") {
.select2-container .select2-choice, select, input, .poke-info label, 
.poke-info input.move-bp, .field-info .btn, fieldset {
 border:1px solid black;
 border-radius:5px;
}

.select2-container .select2-choice, select, input, 
.poke-info label, .poke-info input.move-bp {
  padding:3px 0;
  font-size:11px;
  text-transform:uppercase;
  text-align:center;
}

.poke-info label {
  width:100px;
}

.move-result-group {
  margin:10px 0;
}

.move-result-group .btn {
 border:1px solid black;
}

.move-result-subgroup {
  float:none;
  display:inline-block;
  vertical-align:top;
  width:calc(50% - 3px);
	background:rgba(0,0,0,.2);
  padding:15px 0;
}

.move-result-subgroup > div {
  padding:0 15px;
}

.main-result-group {
  margin: 10px 0;
  padding:20px;
  background:rgba(255,255,255,.05);
}

.wrapper {
  padding-top:30px;
}

.main-title-text {
  display:block;
  text-align:center;
  text-transform:uppercase;
  font-size:30px;
}

.poke-import, .credits, .genSelection, .notationSelection, .modeSelection,
.wrapper hr, .header {
  display:none;
}

span.total {
  font-weight:bold;
  padding-left:3px;
  color:#8d9cc9;
  }
}
