style.css:
/* Cambiar el color de fondo del cuerpo de la página */
body {
    background-color: #f5e1eb; /* rosa claro */
}

/* Cambiar el color del texto */
body, p, h1, h2, h3, h4, h5, h6 {
    color: #6b327c; /* púrpura oscuro */
}

/* Cambiar el color de los enlaces */
a {
    color: #aa4b9d; /* rosa oscuro */
}

/* Cambiar el color de fondo de las áreas resaltadas */
::selection {
    background-color: #aa4b9d; /* rosa oscuro */
    color: #ffffff; /* blanco */
}

/* Cambiar el color de fondo de los botones */
button, input[type="button"], input[type="submit"] {
    background-color: #f5e1eb; /* rosa claro */
    color: #6b327c; /* púrpura oscuro */
    border: 1px solid #6b327c; /* borde púrpura oscuro */
}

/* Cambiar el color del borde de los botones al pasar el cursor */
button:hover, input[type="button"]:hover, input[type="submit"]:hover {
    border-color: #aa4b9d; /* rosa oscuro */
}
