style.css:
/* Estilos por defecto (modo claro) */
:root {
  --body-bg: #F0F0F0;
  --body-color: #000000;
  --header-bg: #E0E0E0;
  --header-color: #000000;
  --main-bg: #FFFFFF;
  --main-color: #000000;
  --section-bg: #F0F0F0;
  --section-color: #000000;
  --link-color: #0000FF;
}

/* Estilos para el modo oscuro */
@media (prefers-color-scheme: dark) {
  :root {
    --body-bg: #000000;
    --body-color: #FFFFFF;
    --header-bg: #202020;
    --header-color: #FFFFFF;
    --main-bg: #101010;
    --main-color: #FFFFFF;
    --section-bg: #000000;
    --section-color: #FFFFFF;
    --link-color: #00FFFF;
  }
}

/* Aplicación de las variables CSS */
body {
  background: var(--body-bg);
  color: var(--body-color);
}

header {
  background: var(--header-bg);
  color: var(--header-color);
}

main {
  background: var(--main-bg);
  color: var(--main-color);
}

section {
  background: var(--section-bg);
  color: var(--section-color);
}

a {
  color: var(--link-color);
}
