style.css:
/* Define blue color */
:root {
  twitter-blue: #1da1f2;
}

/* Apply blue theme to body and links */
body {
  background-color: #fff; /* Twitter's background color */
  color: #000; /* Twitter's default text color */
}

a {
  color: var(--twitter-blue);
}

/* Apply blue theme to buttons */
button,
input[type="submit"],
input[type="reset"],
input[type="button"],
input[type="radio"],
input[type="checkbox"] {
  background-color: var(--twitter-blue);
  border-color: var(--twitter-blue);
  color: #fff; /* White text on blue buttons */
}

/* Apply blue theme to input fields */
input[type="text"],
input[type="email"],
input[type="password"],
textarea {
  border-color: var(--twitter-blue);
}

/* Apply blue theme to hover states */
a:hover,
button:hover,
input[type="submit"]:hover,
input[type="reset"]:hover,
input[type="button"]:hover,
input[type="radio"]:hover,
input[type="checkbox"]:hover {
  background-color: #0a84b4; /* Darker shade of blue for hover effect */
  border-color: #0a84b4; /* Darker shade of blue for hover effect */
}
