style.css:
@-moz-document domain("rezka.ag") {
body.active-brand.fixed-header {
  padding-top: 0 !important;
  color: #fff;
}

#wrapper {
  width: 100% !important;
  background: #444 url(https://img-fotki.yandex.ru/get/9827/161026817.1/0_dd0dc_2bd26520_orig) center fixed;
  color: #777;
}

#main {
  background-color: rgba(0,0,0,0.35);
  box-shadow: 0 0 10px rgba(0,0,0,0.5);
  margin: auto;
  width: 1245px;
}

.b-wrapper {
  width: calc(100% - 30px);
  padding: 0;
}

#cdnplayer-container {
  width: auto !important;
  height: auto !important;
}

#cdnplayer {
  width: 1205px !important;
  height: 700px !important;
}

.b-content__columns {
  width: 100%;
}

.b-post__info,
.b-content__main,
.b-post__info_rates,
.b-post__info_rates a,
.b-post__description_text {
  color: #777;
}

.b-post__info .l,
.b-post__schedule_table,
.b-post__description_title,
.b-comment .info {
  color: #9a9a9a;
}

.comments-form, .addcomment-layer {
  background: none !important;
  background-color: none !important;
}

.b-sidecover {
  padding: 0;
  border: none;
}

#vk_groups, 
.b-post__lastepisodeout, 
.b-post__mixedtext,
.b-post__social_holder_wrapper,
.b-post__actions,
.b-post__rating_table + div,
.b-post__rating_table,
.b-comment__report,
.b-post__schedule {
  display: none;
}

#footer {
  background: none;
  width: 1245px !important;
}

.comments-tree-item, .b-comment__admin, #comments-0 {
  background: none !important;
  border-color: #333;
  border: 1px solid transparent;
  border-color: #333;
  border-radius: 4px;
}

.comments-tree-list {
  background: none !important;
  border-color: #333;
}

.comments-tree-item {
  margin: 10px 10px;
}
}
