style.css:
/* Styles pour Twitter avec des couleurs douces */

/* Fond doux */
body {
    background-color: #f0f0f0;
}

/* Contenu principal */
div[data-testid="primaryColumn"] {
    background-color: #ffffff;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    padding: 10px;
}

/* Liens */
a {
    color: #0077cc; /* Bleu doux pour les liens */
}

/* Barre de navigation sup√©rieure */
div[data-testid="sidebarColumn"] {
    background-color: #ffffff;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    padding: 10px;
}

/* Tweets */
div[data-testid="tweet"] {
    background-color: #f9f9f9;
    border-radius: 10px;
    padding: 10px;
    margin-bottom: 10px;
}

/* Bouton de tweet */
a[data-testid="SideNav_NewTweet_Button"] {
    background-color: #0077cc;
    color: #ffffff;
    border-radius: 25px;
    padding: 10px 15px;
}

/* Barre de recherche */
input[data-testid="SearchBox_Search_Input"] {
    background-color: #f0f0f0;
    border-radius: 20px;
    padding: 8px 12px;
    border: 1px solid #cccccc;
    color: #333333;
}

/* Trends */
div[data-testid="trend"] {
    background-color: #f9f9f9;
    border-radius: 10px;
    padding: 10px;
    margin-bottom: 10px;
}

/* Boutons de suivi */
span[data-testid="follow"] {
    background-color: #0077cc;
    color: #ffffff;
    border-radius: 20px;
    padding: 5px 10px;
    font-size: 14px;
}

/* Survol des boutons */
span[data-testid="follow"]:hover,
a[data-testid="SideNav_NewTweet_Button"]:hover {
    background-color: #005ea3;
}
