style.css:
@-moz-document domain("moodle.innopolis.university") {
@font-face {
        font-family: 'icomoon';
        src: url('https://static.dan.tatar/icomoon.ttf') format('truetype');
        font-weight: normal;
        font-style: normal;
        font-display: block;
    }

    :root {
        --color_primary: #40ba21;
    }

    .header-main {
        background: #111;
        border-bottom: 1px solid #000;
        margin-top: -61px;
    }

    img[alt="IU Moodle"],
    img[alt="Academi"],
    .footer-bootom,
    div[data-region="no-courses-empty-message"] img,
    #header .address-head {
        display: none;
    }

    div[data-region="no-courses-empty-message"] p:before {
        content: "";
        display: block;
        background: url(https://img.icons8.com/fluency/128/confetti.png);
        background-size: 100% 100%;
        width: 64px;
        height: 64px;
        left: 0;
        right: 0;
        margin: auto;
        margin-top: 20px;
        margin-bottom: 10px;
    }

    .logo:before,
    .footer-logo:before {
        display: block;
        content: "\e911";
        font-family: "icomoon";
        font-size: 1.7em;
        color: #fff;
        margin-left: 40px;
    }

    .primary-navigation .navigation .nav-link {
        color: #40ba2136;
    }

    .header-main .navbar-light .primary-navigation .navigation .nav-link:hover,
    .header-main .navbar-light .primary-navigation .navigation .nav-link:focus {
        color: #40ba219e;
        border-color: #40ba2166;
    }

    .course_category_tree .category > .info {
        border-radius: 3px;
    }

    #page.drawers .main-inner,
    #region-main,
    #footer .footer-main,
    body {
        background: #111;
        border-radius: 0;
    }

    .footer-main {
        padding-top: 100px;
    }

    #frontpage-available-course-list,
    #frontpage-course-list,
    #frontpage-category-combo,
    #frontpage-category-names,
    #site-news-forum,
    .card,
    .block .card-body .content,
    .drawer,
    #page,
    .message-app,
    .message-app .list-group-item,
    .popover-region-container {
        border: none;
        background: #111111;
    }

    .subcategories .coursebox > .info > .coursename a,
    #frontpage-category-names .coursebox > .info > .coursename a,
    #frontpage-category-combo .coursebox > .info > .coursename a {
        background-image: url(https://img.icons8.com/fluency/48/recycling.png);
        background-size: 16px 16px;
    }

    .coursebox > .info > .coursename a {
        font-size: 18px;
    }

    .maincalendar .calendarmonth td,
    .maincalendar .calendarmonth th {
        border-style: solid;
        border-color: #1d1d1d;
    }

    .maincalendar .calendarmonth .clickable:hover,
    .block .card-body .content .block-timeline .no-gutters .dropdown-toggle,
    input[type="text"],
    input[type="password"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="date"],
    input[type="month"],
    input[type="time"],
    input[type="week"],
    input[type="number"],
    input[type="email"],
    input[type="url"],
    input[type="search"],
    input[type="tel"],
    input[type="color"],
    .uneditable-input,
    .path-mod .activity-header:not(:empty),
    .btn:not(a[aria-controls="user-action-menu"]),
    .popover-region-footer-container,
    div[data-region="view-conversation"],
    textarea,
    .dropdown-menu,
    table.flexible tbody tr:hover > td,
    table.flexible tbody tr > td,
    .modal-content {
        background: #191919!important;
        background-color: #191919!important;
        border: none;
    }

    .btn:not(a[aria-controls="user-action-menu"]),
    .table,
    .dropdown-item,
    .dropdown-menu,
    table.flexible tbody tr:hover > td {
        color: #aaa;
    }

    .moremenu .nav.nav-tabs,
    .courses .coursebox.even {
        background: transparent;
    }

    .btn,
    a {
        transition: all .2s ease;
    }

    .custom-select {
        background: #191919 url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' width='4' height='5' viewBox='0 0 4 5'%3e%3cpath fill='%23343a40' d='M2 0L0 2h4zm0 5L0 3h4z'/%3e%3c/svg%3e") right .75rem center / 8px 10px no-repeat;
        border: none;
    }

    .card-footer {
        background-color: #191919!important;
        height: 48px;
    }

    .drawer .drawerheader .drawertoggle {
        border: none;
        color: var(--color_primary);
        background: transparent;
    }

    .activity-dates.small,
    button.close:hover {
        filter: invert(1);
    }

    .activity-item:not(.activityinline),
    .btn:not(:disabled):not(.disabled),
    .course-content ul li.section.main,
    .drawers .block_myoverview,
    .coursebox,
    #header,
    .path-mod .activity-information .activity-dates,
    .path-mod .activity-information .completion-info,
    .border-bottom,
    .popover-region-header-container,
    .table th,
    .table td,
    .preferences-container .preference-table tr th,
    .preferences-container .preference-table,
    div[data-region="view-conversation"],
    textarea,
    .dropdown-menu,
    .dropdown-divider,
    .modal-header,
    .mform fieldset,
    .modal-footer {
        border-color: #1d1d1d!important;
    }

    #header .navbar-nav button.btn > span {
        background: transparent;
        filter: invert(1);
    }

    .drawers .block_myoverview {
        border-radius: 3px;
    }

    .secondary-navigation .navigation {
        background: #111;
    }

    .moremenu .nav.nav-tabs .nav-link {
        border-radius: 0;
    }

    .drawercontent::-webkit-scrollbar {
        width: 6px;
    }
    .drawercontent::-webkit-scrollbar-thumb {
        background-color: #222222;
        border-radius: 20px;
    }
    .drawercontent::-webkit-scrollbar-track {
        background: #151515;
    }

    button.btn.btn-outline-secondary.btn-sm.text-nowrap {
        padding-left: 28px;
    }

    button.btn.btn-outline-secondary.btn-sm.text-nowrap:before {
        content: '';
        width: 18px;
        height: 18px;
        position: absolute;
        display: block;
        background: url(https://img.icons8.com/fluency/48/verified-account.png);
        background-size: 100% 100%;
        margin-left: -22px;
    }

    .block .block-cards .dashboard-card-footer.menu {
        opacity: 1;
    }

    #usernavigation {
        position: absolute;
        right: 0;
        margin-right: 20px;
        margin-top: 70px;
    }

    #header {
        height: 0;
        position: absolute;
    }

    .primary-navigation {
        margin-right: 150px;
    }

    .header-main .navbar-light .primary-navigation {
        margin-left: 30px;
    }

    .dashboard-card-deck .dashboard-card,
    .popover-region-container,
    .modal-content,
    input[type="text"],
    input[type="password"],
    input[type="datetime"],
    input[type="datetime-local"],
    input[type="date"],
    input[type="month"],
    input[type="time"],
    input[type="week"],
    input[type="number"],
    input[type="email"],
    input[type="url"],
    input[type="search"],
    input[type="tel"],
    input[type="color"] {
        border: 1px solid #1d1d1d;
    }


    .popover-region-container {
        border-radius: 3px;
        padding: 15px;
    }

    .popover-region-footer-container {
        border-radius: 3px;
        padding: 10px 20px;
        height: 40px;
        margin-top: -10px;
    }

    .popover-region-header-container {
        padding-bottom: 10px;
        height: 35px;
    }




    ::-webkit-scrollbar {
        width: 8px;
        background: #303841;
        height: 8px;
    }
    ::-webkit-scrollbar-track {
        background: #444b53;
        border-radius: 3px;
    }
    ::-webkit-scrollbar-button {
        display: none;
    }
    ::-webkit-scrollbar-corner {
        background: #303841
    }
    ::-webkit-scrollbar-thumb {
        background: #696f75;
        border-radius: 3px;
    }
}
