/*Keyframes*/

@keyframes changeWidthUI {
  from {
    width: 10px;
  }
  to {
    width: 100%;
  }
}

@keyframes revertWidthUI {
  from {
    width: 100%;
  }
  to {
    width: 10px;
  }
}

@keyframes changeNavWidth {
  from {
    width: 48px;
  }
  to {
    width: 9%; /* ou outra porcentagem desejada */
  }
}

@keyframes revertNavWidth {
  from {
    width: 9%; /* ou outra porcentagem desejada */
  }
  to {
    width: 48px;
  }
}

@keyframes changeBarWidth {
  from {
    width: 40px;
  }
  to {
    width: 170%; /* ou outra porcentagem desejada */
  }
}

@keyframes revertBarWidth {
  from {
    width: 170%; /* ou outra porcentagem desejada */
  }
  to {
    width: 40px;
  }
}
@keyframes DarkGradientAnimator {
  from {
    background-image: linear-gradient(140deg, transparent 50%, transparent 100%);
    background-position: 0px 150px;
    opacity: 0.2;
    border: 1px solid transparent
  }
  to {
    background-image: linear-gradient(140deg, transparent 50%, #ffffff5e 100%);
    background-position: 0px 10px;
    opacity: 1;
    border: 1px solid #ffffff5e;
    backdrop-filter: blur(50px);
    padding: 5px;
    box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
  }
}

@keyframes DarkGradientAnimatorReverse {
  from {
    padding: 5px;
    background-image: linear-gradient(140deg, transparent 50%, #ffffff5e 100%);
    background-position: 0px 10px;
    opacity: 1;
    border: 1px solid #ffffff5e;
    box-shadow: 0 0 10px rgba(255, 255, 255, 0.5);
  }
  to {
    background-image: linear-gradient(140deg, transparent 50%, transparent 100%);
    background-position: 0px 150px;
    opacity: 0.2;
    border: 1px solid transparent
  }
}
@keyframes LightGradientAnimator {
  from {
    background-image: linear-gradient(140deg, transparent 50%, transparent 100%);
    background-position: 0px 150px;
    opacity: 0.2;
    border: 1px solid transparent
  }
  to {
    background-image: linear-gradient(140deg, transparent 50%, #393b3d 100%);
    background-position: 0px 10px;
    opacity: 1;
    border: 1px solid #393b3d;
    backdrop-filter: blur(50px);
    padding: 5px;
    box-shadow: 0 0 30px rgba(0, 0, 0, 0.5);
  }
}

@keyframes LightGradientAnimatorReverse {
  from {
    padding: 5px;
    background-image: linear-gradient(140deg, transparent 50%, #393b3d 100%);
    background-position: 0px 10px;
    opacity: 1;
    border: 1px solid #393b3d;
    box-shadow: 0 0 30px rgba(0, 0, 0, 0.5);
  }
  to {
    background-image: linear-gradient(140deg, transparent 50%, transparent 100%);
    background-position: 0px 150px;
    opacity: 0.7;
    border: 1px solid transparent
  }
}
