style.css:
@-moz-document url-prefix("https://character.ai") {
/* Color italicized text */
	em {
  		color: #8A88AB; /* change this to the color you want */
	}
	/* Color bold text */
	strong {
	  	color: #8A88AB !important; /* change this to the color you want */
	}

	/* Remove the [c.ai] tag by the name so you can pretend
	* you're not a cripplingly lonely person talking to an AI */
	.group .mx-2 > .rounded-2xl {
	 	  display: none;
	}

	/* Hide "Edited" on edited messages */
	p[title="Message edited by user"] {
  		display: none;
  }
}
