style.css:
div[data-macro-name="numberedheadings"] h1,
div[data-macro-name="numberedheadings"] h2,
div[data-macro-name="numberedheadings"] h3,
div[data-macro-name="numberedheadings"] h4,
div[data-macro-name="numberedheadings"] h5,
div[data-macro-name="numberedheadings"] h6 {
  white-space: pre-wrap;
  font-size: 1.25rem !important;
  font-weight: normal;
}

div[data-macro-name="numberedheadings"] h1::before {
  content: '• ';
}

div[data-macro-name="numberedheadings"] h2::before {
  content: '    • ';
}

div[data-macro-name="numberedheadings"] h3::before {
  content: '        • ';
}

div[data-macro-name="numberedheadings"] h4::before {
  content: '            • ';
}

div[data-macro-name="numberedheadings"] h5::before {
  content: '                • ';
}

div[data-macro-name="numberedheadings"] h6::before {
  content: '                    • ';
}
