style.css:
@-moz-document url-prefix("https://www.youtube.com/watch"), url-prefix("https://youtube.com/watch") {
/* 你的CSS样式 */
.ytd-comment-renderer
{
    font-size: 2.0rem !important;
    line-height: 2.5rem !important;
    font-weight: 300 !important;
    letter-spacing: 0.03rem !important;
}
  
.ytd-comment-view-model
{
    font-size: 2.0rem !important;
    line-height: 2.5rem !important;
    font-weight: 300 !important;
    letter-spacing: 0.03rem !important;
}

  
yt-attributed-string.ytd-comment-view-model
{
    font-size: 2.0rem !important;
    line-height: 2.5rem !important;
    font-weight: 300 !important;
    letter-spacing: 0.03rem !important;
}

h3.ytd-comment-view-model {
    opacity: 0.2;
}

div.ytd-comment-engagement-bar {
    opacity: 0.2;
}

div.ytd-comment-replies-renderer {
    opacity: 0.6;
}

.ytd-text-inline-expander
{
    font-size: 1.6rem !important;
    line-height: 2.0rem !important;
    font-weight: 300 !important;
    letter-spacing: 0.03rem !important;
}


.glarity--content
{
    font-size: 1.6rem !important;
}

#dual-sub {
  width: 100% !important;
}

#subtitleContainer {
   width: 100% !important;
  max-width: 100% !important;
  margin: 0 auto;
    left: 0 !important;
  text-align: center;
}

#subtitle {
  width: 90% !important;
  margin: 0 auto;
}

.text-selection {
    line-height: 1.3em;
    width: 100%;
    color: #EEEEEE;
    font-weight: bold;
    text-shadow:
        1px 1px 0 #333,
        -1px -1px 0 #333,
        1px -1px 0 #333,
        -1px 1px 0 #333,
        2px 2px 4px rgba(20, 20, 20, 0.9);
}
}
