@-moz-document domain("mail.google.com") {
/* Loading screen color inversion */
  #loading {
    filter: invert(0.9) hue-rotate(180deg);
  }

  #loading img {
    filter: invert(1) hue-rotate(-180deg);
  }

  /* Allow left sidebar to be as narrow as its content */
  .aeN,
  .wT {
    min-width: auto !important;
  }

  /* Selected email highlighting */
  .aps {
    color: white;
    background: rgba(135, 135, 135, 0.2);
    border: 1px solid rgba(77, 144, 240, 0.88);
  }

  /* Email rows */
  .yO {
    background: rgba(0, 0, 0, 0.12);
  }

  /* Remove background from email body */
  .nH.id .iY {
    background: rgba(255, 255, 255, 0.3);
  }

  /* Email body color inversion */
  .nH.id.UG {
    background: none;
  }
  .nH.id .iY {
    filter: invert(1) hue-rotate(180deg);
    background-color: rgba(255, 255, 255, 0.3);
    color: #111;
  }

  /* Fix inverted images */
  .nH.id img.ajn,
  .ii :not(table) img {
    filter: invert(1) hue-rotate(-180deg);
  }

  /* Remove email HTML element bgs */
  .ii * {
    background: none !important;
    background-color: none !important;
  }

  /* Un-invert email table elements */
  .hx.adn table {
    filter: invert(1) hue-rotate(-180deg);
  }

  /* Fix for buttons on compose form appearing white on black */
  .nr,
  .Ar {
    background: none;
    color: #222;
  }

  /* Calendar event email header bg */
  .aRi,
  .aU4,
  .aRc {
    background-color: rgba(255, 255, 255, 0.3);
  }

  /* Order tracking email header bg */
  .qY {
    background-color: rgba(255, 255, 255, 0.3);
  }

  /* Agenda label colors */
  .aRk,
  .aQ4,
  .aQ5 {
    color: #0f17ff;
  }

  /* Notification inlay bg */
  .adp,
  .h9,
  .aHn {
    background-color: rgba(173, 169, 169, 0.21);
    border: 1px solid rgba(100, 100, 100, 0.28);
    border-radius: 5px;
    color: #444;
  }

  /* Fix "important" chevron */
  .pH.a9q {
    opacity: 1;
    filter: none;
  }
  .ha .a9q.pH {
    opacity: 1;
    filter: invert() hue-rotate(180deg);
  }

  /* Fix "starred" icon */
  .T-KT {
    filter: hue-rotate(180deg);
  }
  .apU > .T-KT.T-KT-Jp::before,
  .bi4 > .T-KT.T-KT-Jp::before {
    opacity: 1;
  }
  .bi4 > .T-KT {
    filter: invert(1);
  }

  /* Agenda date color */
  .aRn {
    color: #0f30ff;
    margin-bottom: 14px;
  }

  /* Hide sidepanel button */
  .brC-dA-I-Jw {
    display: none;
  }

  /* Fix for tracking links */
  .mUIrbf-I {
    -webkit-padding-start: auto;
    padding-inline-start: auto;
    -webkit-padding-end: auto;
    padding-inline-end: auto;
    block-size: auto;
  }

  /* Fix for tracking links */
  .bjL {
    background-color: rgba(173, 169, 169, 0.21);
    border: 1px solid rgba(100, 100, 100, 0.28);
  }

  /* Fix for inline email compose body */
  .HM .GQ {
    background-color: hsl(0deg, 0%, 100%, 8%);
    padding-top: 20px !important;
  }
  .HM .IG {
    background-color: rgba(0, 0, 0, 0.12);
  }
  .Ap,
  .HM .ahe::after {
    background: transparent;
  }

  /* Inline editor controls */
  .aDg > .aDj {
    background: rgba(0, 0, 0, 0.12);
    position: relative !important;
    clip: auto !important;
    top: 0px !important;
    left: 0px !important;
    right: 0px !important;
    bottom: 0px !important;
    width: auto !important;
  }

  .azy,
  .azY {
    position: relative !important;
    clip: auto !important;
    top: 0px !important;
    left: 0px !important;
    right: 0px !important;
    bottom: 0px !important;
  }

  /* Fix glitchy compose overlay */
  .aC2,
  .aC4 {
    display: none;
  }

  /* Fix inline compose bg */
  .Al,
  .aoP .Ar {
    background: none;
  }

  /* Inline composer border */
  .HM .I5 {
    border: none;
  }
  .HM .I5:hover {
    border: none;
  }
  /* Fix inline email clipping */
  .vy .S3 {
    overflow-x: hidden;
  }
  /* Contact bubble */
  .afV {
    background: none;
    box-shadow: 1px 1px 0px 0px rgb(0 0 0);
    color: rgb(40 40 40);
    border: 1px solid rgba(0, 0, 0, 0.59);
    border-right: none;
    border-bottom: none;
  }
  .afV:hover {
    background: rgba(255, 255, 255, 0.1);
  }
  .eV * {
    background-color: transparent;
  }

  .Nu {
    overflow-y: auto;
  }
  .HM .I5,
  .HM .I5:hover {
    box-shadow: none;
  }
}
