style.css:
@-moz-document domain("msnp.knutd.edu.ua") {
/* main site logo */
  .logo.mr-1 { content: url("https://avatarfiles.alphacoders.com/373/373815.png"); }

  /* base styles */
  /* color pallete
  1: #222831 | black
  2: #31363F | gray
  3: #76ABAE | green
  4: #EEEEEE | white */

  div[id=page-wrapper], .region-main, .columnleft, .columnright, section[id=region-main] { background-color: #222831 !important; border: none !important; box-shadow: none !important; }
  div[role=main], #page-header .card { background-color: #31363F !important; }
  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 { color: #76ABAE !important; }
  div[role=main] p  { color: #EEEEEE }
  nav.navbar, nav.navbar a { background-color: #76ABAE !important; color: #31363F !important; border: none !important; }
  footer#page-footer { background-color: #76ABAE !important; color: #31363F !important; }
  section.block_site_main_menu, section.block_navigation, section.block_myprofile, section.block_calendar_month, section.block_myoverview, section.block_private_files, section.block_recentlyaccesseditems, section.block_timeline, section.block_recentlyaccessedcourses, section.node_category, section.block_settings { background-color: #31363F; color: #EEEEEE; }
  section.block_site_main_menu .activity-item { border-color: #76ABAE; }
  section.block_site_main_menu .activity-item a { color: #76ABAE !important; }
  .dropdown-menu { background-color: #31363F !important; }
  .dropdown-menu .dropdown-divider { border-color: #222831 !important; }
  .dropdown-menu a.dropdown-item { background-color: #31363F !important; color: #76ABAE !important;}
  .dropdown-menu a.dropdown-item:hover { background-color: #76ABAE !important; color: #31363F !important;}

  /* form control styles */
  input.form-control { background: #31363F !important;  color: #EEEEEE !important; }
  input.form-control:focus { background: #76ABAE !important; box-shadow: none !important;border-color: #EEEEEE !important; color: #EEEEEE !important; }
  button.btn { background-color: #76ABAE !important;  color: #EEEEEE !important; border: 1px solid #76ABAE; box-shadow: none !important }
  button.btn:focus { background-color: #EEEEEE !important; border-color: #EEEEEE !important; color: #31363F !important; }
  select { background-color: #76ABAE !important; box-shadow: 0 0 0 2px #76ABAE !important; color: #EEEEEE !important; }
  .maincalendar .calendarmonth .clickable:hover { background-color: #76ABAE !important; color: #31363F !important; }
  ul.pagination li a { background-color: #76ABAE !important; border: 5px solid #76ABAE !important; box-shadow: none !important;}
  ul.pagination li a:focus { border-color: #EEEEEE; }
  ul.pagination li a span { color: #EEEEEE; }
  ul.pagination li.disabled a span { color: #31363F; }
  div[data-region=recentlyaccesseditems-view-content] a.card { background-color: #76ABAE !important; }
  div[data-region=recentlyaccesseditems-view-content] a.card h6 { color: #31363F !important; }
  div[data-region=recentlyaccesseditems-view-content] a.card small { color: #EEEEEE !important; }

  /* / | my pages styles */
  .list-group-item { background-color: #31363F; color: #31363F; }
  .card-deck .card { background-color: #76ABAE !important; }
  .card-deck .card a { color: #31363F !important; }
  .card-deck .card .text-muted span { color: #EEEEEE !important; }
  .card-deck .card .card-footer { background-color: #76ABAE !important; }
  .card-deck .card .card-footer .progress-text { color: #EEEEEE !important; }
  .card-deck .card .card-footer button.btn { border: none; }
  .card-deck .card .card-footer button.btn:focus { background-color: #31363F !important; color: #EEEEEE !important; } 
  .card-deck .card .card-footer a { color: #76ABAE !important; }
  .card-deck .card .card-footer a:hover { color: #31363F !important; }

  /* /mod/page/view.php?id=88757 | payments styles */
  div[role=main] div.box.py-3.generalbox.center.clearfix p, div[role=main] div.box.py-3.generalbox.center.clearfix span { background-color: #31363F !important; }

  /* /course/view.php | courses info styles */
  div[data-for=sectioninfo] p, div[data-for=sectioninfo] span { color: #EEEEEE !important; }

