style.css:
@-moz-document domain("music.yandex.ru") {
/* Общие стили скролл-бара */
    ::-webkit-scrollbar {
        width: 13px; /* Увеличенная ширина скролл-бара */
        background-color: #333; /* Цвет фона скролл-бара */
        border-radius: 8px; /* Радиус скругления */
        transition: width 0.3s; /* Плавное изменение ширины */
    }

    /* Стили ползунка скролл-бара */
    ::-webkit-scrollbar-thumb {
        background-color: #555; /* Цвет ползунка */
        border-radius: 8px; /* Радиус скругления */
        transition: background-color 0.3s; /* Плавный переход цвета */
    }

    /* Эффект увеличения при наведении на ползунок скролл-бара */
    ::-webkit-scrollbar-thumb:hover {
        background-color: #777; /* Цвет ползунка при наведении */
        border-radius: 10px; /* Увеличение радиуса скругления при наведении */
    }

    /* По необходимости можно добавить еще стили */
}
