style.css:
@-moz-document domain("music.yandex.ru") {
:root {
  --color-inactive-switch-blue: #9342e3; 
  --color-blue: #007bff;
    /* Цвета по дефолту */
  --color-inactive-switch-yellow: #ff8c00; /* Основной цвета */
  --color-yellow: #ffcc00; /* Основной цвета */

  --color-inactive-switch-dark: #777; 
  --color-dark: #555;
}

/* Основной стиль заднего фона для скролл-бара */
::-webkit-scrollbar {
    width: 13px;
    background-color: #333;
    border-radius: 8px;
    transition: width 0.3s;
}

/* Ползунок */
::-webkit-scrollbar-thumb {
    background-color: var(--color-yellow); /* Можно изменить на другой цвет из списка выше, к примеру:background-color: var(--color-dark); */
    border-radius: 8px;
    transition: background-color 0.3s;
}

::-webkit-scrollbar-thumb:hover {
    background-color: var(--color-inactive-switch-yellow); /* Можно изменить на другой цвет из списка выше, к примеру:background-color: var(--color-inactive-switch-dark; */
    border-radius: 10px;
}
}
