style.css:
@-moz-document domain("telegra.ph") {
*::-webkit-scrollbar {
		width: 12px;
	}

	*::-webkit-scrollbar-track {
		background-color: #000000;
	}

	*::-webkit-scrollbar-thumb {
		background-color: #d8aaff;
		border-radius: 10px;
		transition: background-color 0.3s;
	}

	*::-webkit-scrollbar-thumb:hover {
		background-color: #4B0082;
	}

	::selection {
		background-color: #d8aaff !important;
		color: #ccc;
		transition: background-color 0.3s;
	}

	::-moz-selection {
		background-color: #d8aaff !important;
		color: #ddd !important;
		transition: background-color 0.3s;
	}

	body, html, .tl_article .tl_article_content figcaption, .tl_article .tl_article_content figcaption .editable_input {
		background-color: #000000;
		color: #d8aaff;
		font-weight: 600;
		transition: background-color 0.3s, color 0.3s;
	}

	.button {
		background-color: #000000;
		color: #800080;
		border: 2px solid #d8aaff;
		font-weight: 600;
		transition: color 0.3s, background-color 0.3s;
	}

	.button:hover {
		color: #4B0082;
		background-color: #000000;
	}

	a, a strong {
		color: #cacaca !important;
		text-decoration: none;
		position: relative;
		transition: color 0.3s;
	}

	a::after, a strong::after {
		content: "";
		position: absolute;
		width: 100%;
		height: 2px;
		background-color: transparent;
		bottom: 0;
		left: 0;
		visibility: hidden;
		transform: scaleX(0);
		transition: transform 0.3s ease-in-out, background-color 0.3s, visibility 0.3s;
	}

	a:hover::after, a strong:hover::after {
		visibility: visible;
		transform: scaleX(1);
		background-color: #d8aaff;
	}

	h1 {
		color: #ffffff !important;
		transition: color 0.3s;
	}

	h2, h3, h4, h5, h6 {
		color: #b9b9b9 !important;
		transition: color 0.3s;
	}

	p, .tl_article .tl_article_content, .tl_article .tl_article_content .ql-editor *, .tl_article.tl_article_edit [data-placeholder].empty:before, .button, .tl_article.tl_article_edit figcaption[data-placeholder].empty:after, .annotation {
		color: #a0a0a8;
		transition: color 0.3s;
	}

	.annotation {
		color: #73d794;
	}

	.tl_article address, .tl_article address a, address a {
		color: #d8aaff !important;
		transition: color 0.3s;
	}

	.tl_article .tl_article_content blockquote {
		border-color: #d8aaff;
		transition: border-color 0.3s;
	}

	img {
		border: 1px solid #d8aaff;
		transition: border-color 0.3s;
	}

	img:hover {
		border-color: #4B0082;
	}

	figure {
		margin: 10px;
		position: relative;
	}

	figure iframe {
		border: 2px solid #d8aaff;
		border-radius: 0px;
		width: 100%;
		height: 100%;
	}

	figure figcaption {
		padding: 10px;
		text-align: center;
		background-color: #000;
		color: #d8aaff;
	}

	figure:hover figcaption {
		background-color: #4B0082;
	}

	figure:hover {
		border-color: #8A2BE2;
	}

	#_report_button {
		background-color: #1C1C1C;
	}
}
