style.css:
body {
    margin: 0; /* Rimuove il margine predefinito */
    padding: 0; /* Rimuove il padding predefinito */
}

/* Rimuove i bordi laterali */
body::before,
body::after {
    content: "";
    position: fixed;
    top: 0;
    height: 100%;
    width: 20px; /* Larghezza del bordo laterale da rimuovere */
    background-color: var(--colore-di-sfondo); /* Cambia con il colore di sfondo del tuo tema */
}

body::before {
    left: 0;
}

body::after {
    right: 0;
}

/* Rimuove il bordo inferiore */
footer {
    display: none;
}
