/* ==UserStyle==
@name           Discord Blue Accent Dark Theme
@namespace      https://discordapp.com
@version        1.0
@description    Adds blue accents to Discord's default dark theme.
@author         Your Name
==/UserStyle== */

/* Main background color */
:root {
    --background-primary: #36393f;
    --background-secondary: #2f3136;
    --background-secondary-alt: #292b2f;
    --background-tertiary: #202225;
    --background-accent: #4f545c;
}

/* Text colors */
:root {
    --text-normal: #ffffff;
    --text-muted: #b9bbbe;
}

/* Accent colors */
:root {
    --brand-color: #7289da; /* Blue */
}

/* Accent background colors */
:root {
    --brand-color-hover: #677bc4; /* Slightly darker blue on hover */
}

/* Channel and category colors */
:root {
    --channel-default: #202225;
    --channel-hover: #32353e;
    --channel-selected: #7289da; /* Blue */
    --channel-unread: #5865f2; /* Lighter blue for unread channels */
}

/* Server list colors */
:root {
    --server-selected: #5865f2; /* Blue */
    --server-hover: #677bc4; /* Slightly darker blue on hover */
}

/* User status colors */
:root {
    --status-online: #43b581; /* Green */
    --status-idle: #faa61a; /* Yellow */
    --status-dnd: #f04747; /* Red */
    --status-invisible: #747f8d; /* Gray */
    --status-streaming: #593695; /* Purple */
}

/* Role colors */
:root {
    --role-color: #7289da; /* Blue */
}
