style.css:
@-moz-document url-prefix("https://akasha.cv/") {
.navbar {
  background-color:#00523a;
}
.navbar .active-tab{
  background-color:#29775a !important;
}

.navbar .active-tab:hover{
  background-color:#26674d !important;
}
.navbar-tab > a {
  background-color:#00523a;
}
.navbar-tabs .navbar-tab:hover{
	background-color:#025940;
}

.active-tab > .close-tab {
	background-color:#00523a;
}
.navbar-tabs .navbar-tab:not(.active-tab), .navbar-tabs .navbar-tab:not(.active-tab) .close-tab, .navbar-tabs .navbar-tab:not(.active-tab) a {
  background-color:#013b2a;
}
.navbar-tabs .navbar-tab .close-tab, .navbar-tabs .navbar-tab a{
  background-color:#00523a;
}
.navbar-tabs .navbar-tab:hover .close-tab, .navbar-tabs .navbar-tab:hover a
{
  background-color:#02694b;
}
.navbar > a:hover {
	  background-color:#02694b;
}
.language-switcher > a:hover {
	 background-color:#02694b;
}

.current-langauge {
	background-color:#29775a !important;
}
.language-dropdown > div {
	background-color:#00523a;
}
.language-dropdown > div:hover, .language-switcher .language-dropdown>div.current-langauge:hover {
background-color:#02694b !important;
}
/*NAVBAR END*/

.modal{
  border-radius:10px;
  overflow:hidden;
}

.modal-header{
  background-color:#00523a !important;
}
.modal-content{
    background: linear-gradient(13deg, #001a12 50%, #092921);
}
/*LOGIN END*/

#root {
  background: linear-gradient(13deg, #001a12 50%, #092921);
  min-height:100vh;
}

.stylized-content-block-wrapper:after{
  background:linear-gradient(rgba(2, 79, 56,.867) 5%,rgb(2, 31, 22) 80%);
}

.stylized-content-block-wrapper.gradient:after{
   background:linear-gradient(rgba(2, 79, 56,.867) 5%,rgb(2, 31, 22) 80%);
}

.content-block{
  background:none;
  border-radius:15px;
}

.content-wrapper{
  min-width:1200px;
  width:60vw;
  max-width:75vw !important;
}
.content-wrapper >div{
  background-size:cover;
}
.content-container>div{
  background-color:transparent;
}

html{
  background-color:#0d231d;
}

.profile-header-wrapper, .custom-table-wrapper
{
  margin:3px;   
}
.profile-header-wrapper div, .custom-table-wrapper div
{
  border-radius:5px;
}
.stylized-content-block-wrapper .stylized-content-block {
  border-radius:15px !important;
}
.stylized-content-block
{
  height:100% !important;
}
.stylized-content-block-wrapper:after{
    height:100% !important;
}
.stylized-content-block-wrapper .stylized-content-block{
  background-size:cover;
}

a{
      color:#2da64b;
}
a:active{
    color:#218a3b;
}
.pagination-wrapper{
  margin-bottom:-15px;
}

.block-highlight {
	border-radius:10px;
}

.other-calculations-display{
  height:fit-content !important;
  font-size:small;
}
/*GENERAL END*/

.badges-container
{
  width:100%;
	display:grid;
  place-content:start;
}

.genshin-card-content{
  padding:5px;
  border-left: solid 1px gray;
  border-radius:0px !important;
}

.fav-btn  {
  position:absolute;
  right:0px;
}
.fav-btn  svg{
  position:absolute;
  right:13px;
  top:13px;
}

.uid-result > .genshin-user-card-wrapper{
  width:fit-content
}
.scrollbar-container .highlight-tile-container{
  position:relative;
  top:0;

}

.highlight-tile-container .matching-weapon,.highlight-tile-container .mismatching-weapon,.highlight-tile-container .almost-matching-weapon {
	height:120px;

}
.highlight-tile-container .highlight-tile{
  height:100%;
  width:90px;
 
}

.highlight-tile-container  div{
  font-size:larger;
}
.stacked-top{
  width:100%;
  font-size:small !important;
  
}
.matching-weapon .flex > .table-icon,.matching-weapon .table-icon-text-pair > .table-icon,.mismatching-weapon .flex > .table-icon,.mismatching-weapon .table-icon-text-pair > .table-icon,.almost-matching-weapon .flex > .table-icon,.almost-matching-weapon  .table-icon-text-pair > .table-icon {
	height:34px;
  width:34px;
  margin-top:3px;
  margin-bottom:5px;
}
.matching-weapon div, .mismatching-weapon div, .almost-matching-weapon div {
	height:100%;
}
.matching-weapon a , .mismatching-weapon a, .almost-matching-weapon a {
	border-radius:5px 
}
.highlight-tile span{
 margin-bottom:10px !important;
}
.ps__thumb-x{
  height:10px !important;
  z-index:1 !important
}
.ps__rail-x{
  height:15px;
    z-index:1

}

.stylized-content-block-wrapper.gradient-reverse:after{
    background:linear-gradient(rgba(2, 79, 56,0.4) 5%,rgba(2, 31, 22,0.75) 80%);
}

.current-profile{
	border-radius:5px
}
.profile-selector-tile> .profile-selection-item-wrapper {
	border-radius:5px
}

.expanded-showcase .highlight-tile{
  width:100%
}
.highlight-tile-container{
  height:fit-content;
    min-width: 100%;
    justify-content: space-between;
}
/*CONFIRM TOOLTIP PART*/
.matching-weapon> div>.relative, .mismatching-weapon >div>.relative, .almost-matching-weapon> div>.relative {
  height:0;
}
.confirm-tooltip{
  background-color:#00523a;
  font-size:13px !important;
  height:29px !important;
  width:-webkit-max-content !important;
  width:max-content !important;
  margin-right:-90px!important;
}
.confirm-buttons{
  height:29px !important
}
.confirm-buttons >div{
  padding:5px !important;
  padding-top:2px!important;
  padding-right:7px!important;
   vertical-align:center
}
.confirm-tooltip-text
{
  margin-top:5px
}

.highlight-tile-container.expanded-showcase {
   
  grid-template-columns: repeat(auto-fit, minmax(100px, 1fr));
}
/*CONFIRM TOOLTIP END*/
/* PROFILE END*/
/*FILTER START*/
.css-1nvinoy-multiValue{
  background-color:#00523a;
}
.css-eitj2c-menu, .css-1vco2lh-option{
  background-color:#001a12;
}
.css-1vco2lh-option{
  width:33%;
}
.css-1lyd17v-option{
 background-color:rgba(0, 131, 0, 0.333);
  width:33%;
}
.css-1s9izoc div:nth-child(2){
  display:flex;
  flex-wrap:wrap;
}
.css-1s9izoc {
  font-size:larger;
}
/*FILTER END*/


/*SCROLLBAR START*/
::-webkit-scrollbar {
  width: 10px;
}

::-webkit-scrollbar-track {
  background: none;
}

::-webkit-scrollbar-thumb {
  background: gray; 
  border-radius: 10px;
}
::-webkit-scrollbar-thumb:hover {
  background: darkgray; 
}
  /*SCROLLBAR END*/
.table-icon, .overlay-icon, .lb-badge .weapon-icon{
    filter: drop-shadow(0px 0px 2px black) !important;
}
.genshin-user-card-wrapper.clickable-card:hover .card-background {
    box-shadow: inset 0 200px 0 0 rgba(0, 0, 0, .5);
}
@media only screen and (max-width:1300px){
  .genshin-user-card-wrapper{
  	width:100%
  }
}
}
