style.css:
/* i really want this to be global */

.ac-pushButton {
background-color: rgb(17, 94, 163);
  color: white; /* White text */
  border: 2px solid #0078D4; /* Blue border, same as the background for a solid look */
  border-radius: 5px; /* Rounded corners, adjust as necessary */
  padding: 10px 20px; /* Top and bottom padding, Left and right padding */
  font-family: "Segoe UI", "Segoe UI Web (West European)", -apple-system, BlinkMacSystemFont, Roboto, "Helvetica Neue", sans-serif;
  font-size: 16px; /* Font size, adjust as necessary */
  font-weight: bold; /* Makes the text bold */
  text-align: center; /* Center the text inside the button */
  cursor: pointer; /* Changes cursor to a hand symbol when hovered */
  text-decoration: none; /* Removes underline from text */
  display: inline-block; /* Allows setting padding and width/height */
  margin: 4px 2px; /* Space around the button, adjust as necessary */
  -webkit-transition: background-color 0.3s; /* Smooth background color transition */
  transition: background-color 0.3s; /* Smooth background color transition for non-webkit browsers */
  height:30px;
}

.agent-execution-success
{
  background-color: rgb(208,255,188) !important;
}


.style-positive 
{
  background: none;
  color: white;
  border:none;
  position:absolute;
  bottom:-25px;
  right:5%;
}

.style-destructive
{
  background: none;
  color: white;
  border:none;
  position:absolute;
  bottom:-25px;
  right:0%;
}

#aigeneratedtext.ac-textBlock {
    background: none;
  color: white;
  border:none;
  position:absolute;
  bottom:-10px;
  right:20%;
}

#lastupdated.ac-textBlock {
    background: none;
  color: white;
  border:none;
  position:absolute;
  bottom:-10px;
}

.ac-textInput {
  background-color: #F3F2F1; /* Fluent UI default background color for inputs */
  border: 0px solid #A6A6A6; /* Matching Fluent UI border color */
  border-radius: 2px; /* Fluent UI uses 2px as standard border-radius for inputs */
  font-family: 'Segoe UI', 'Helvetica Neue', Arial, sans-serif; /* Fluent UI's default font stack */
  font-size: 14px; /* Commonly used font-size in Fluent UI for input text */
  padding: 10px 12px; /* Standard padding for Fluent UI inputs */
  width: 100%; /* Full width to fit container, can be adjusted as needed */
  box-sizing: border-box; /* Padding and border are included in width and height */
  margin-bottom: 10px; /* Space below each input, adjust as needed */
  color: #323130; /* Fluent UI default text color */
}

.accordion__button {
display:none;
}

.ac-multiline {
  min-height: 100px; /* Minimum height */
  width: 100%;
  resize: vertical;
  overflow-y: auto;
  box-sizing: border-box;
}

.adaptivecardcontrol { 
    position: relative;
    width: 100%; /* Adjust based on your design requirements */
    height: auto; /* Adjust based on your design requirements */
    overflow: hidden;
  background-color: white; /* White background for the card */
  border-radius: 8px; /* Adjust as necessary for rounded corners */
  box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2); /* Horizontal offset, vertical offset, blur radius, and color of the shadow */
  padding: 20px; /* Space inside the card, adjust as necessary */
  margin: 20px; /* Space outside the card, to avoid touching other elements or the edge of the screen */
  /* Optional: If you want a max-width or responsiveness */
  max-width: 100%; /* Ensures the card won't exceed the width of its container */
}

.carousel-arrow {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    background-color: #0078d4;
    color: black;
    cursor: pointer;
    z-index: 1000;
}

.prev-arrow {
  margin-left:-20px;
}

.next-arrow {
margin-right:-20px;
}
