style.css:
@-moz-document url-prefix("https://u.geekbang.org/lesson/") {
.lesson-wrapper {
        margin: 0 auto !important;
    }

    .lesson-inner {
        width: 100% !important;
    }

    .playerWrap {
        height: auto !important;
    }

    .lesson-section.playerWrap {
        padding: 0 !important;
    }

    div[id^="aliyunPlayerCon"] video {
        max-height: 1050px !important;
    }

    div.gkplayer[class*="GkPlayer_full"] video {
        max-height: 100% !important;
    }

    .poster {
        height: auto !important;
    }
}
