style.css:
/* Colores base */
body {
  background-color: #222;
  color: #eee;
}

a {
  color: #eee;
}

/* Elementos de formulario */
input,
textarea,
select {
  background-color: #333;
  color: #eee;
}

/* Enlaces visitados */
a:visited {
  color: #999;
}

/* Enlaces hover */
a:hover {
  color: #fff;
}

/* Botones */
button {
  background-color: #444;
  color: #eee;
  border: none;
}

/* Cabeceras */
h1,
h2,
h3,
h4,
h5,
h6 {
  color: #fff;
}

/* CÃ³digo */
code {
  background-color: #444;
  color: #f8f8f8;
}

/* Tablas */
table {
  background-color: #333;
  color: #eee;
}

/* Celdas de tabla */
th,
td {
  border: 1px solid #555;
}

/* Fondos de fila alternativa */
tr:nth-child(odd) {
  background-color: #444;
}
div {
  background-color: #444;
}
