/*navigation bar*/

/*battlefield background*/
  .battlefield{
border:4px solid #A89899;

  opacity:1;
  /*width: 600px;*/
  /*height: 200px;*/
  /*display:flex;*/
  background-color:red;
  background-size: cover;
  background-image: url('https://steamuserimages-a.akamaihd.net/ugc/1008187258924368621/569D4E8F887B7619EE9E41A0659C0A42A26EC5CE/');
  background-position: center;
  overflow:hidden;
  box-shadow: 0px 0px 50px rgba(255, 100, 100, 0.8);
  /*background: linear-gradient(to right, #ff8a00, #e52e71, #8a2be2, #4b0082, #00ced1, #ff8a00);*/
  /*animation: gradientAnimation 6s ease infinite; /* Adjust ani*/
}

/*playmat noise effect*/
.battlefield::before{
  /*content:'';*/
  opacity:0.4;
  background-position:left;
  position:absolute;
  top:0;
  left:000%;
width: 100%;
  height: 100%;
  background-blend-mode:multiply;
  background: linear-gradient(to right, #32341F32, transparent),
    url(https://grainy-gradients.vercel.app/noise.svg);
  filter: contrast(1%) brightness(50%);
  z-index:0;
  }
  
  
div>.battlefield{
  border:4px solid #A89899;
}

/*navigation bar buttons*/
  
  
  
.player {
	background-color:#35131ab8;
}

.hand {
  border: 8px dotted #573518e3; 
	background-image:  url('https://u-static.fotor.com/images/text-to-image/result/PRO-b421c0cdd983400b9c0e7e09a143ae1f.jpg');
  background-size:1380px 760px;
  border-radius:20px 100px 100px 10px;
  background-position:center;
  }
  
div.d-none.d-md-inline-block:before{
  content:'';
  position:absolute;
  opacity:0.5;
  top:0%;
  left:0%;
  width:300%;
  height:150%;
  background-size:250% 100%;
  background-position:right;
  /*background-position:center;*/
  background-image:  radial-gradient(circle at center right, #FFFFFF,#AA49AA88,#AF75AF78,transparent);
    /*radial-gradient(ellipse at left, #FFFFBB78, transparent),*/
            /*radial-gradient(circle at bottom, #AA34AA66, transparent);*/
	/*background-image: radial-gradient(circle,#EEBCEEFF, #52125634, #AACCBBCC ); /* Initial circular gradient */
  animation: foil 5s ease-in-out infinite; 
}


  
/* Animation keyframes */
@keyframes fly {
    0% {
      	scale:0.2;
        transform: translate(0, 50%);
    }
    100% {
      scale:1;
      opacity:0;
        transform: translate(0%, 0%) ;
    }
}

.mana-cost.mana-shadow{
  box-shadow: 0em 0em 15px 10px #5d08088c, inset 0px 0 2px 2px #5f0c0ccf;
}
  
  
@keyframes foil{
  0% {
    /*left:-40%;*/
    background-position:right;
    }
  50%{
    /*top:0%;*/
    /*left:-40%;*/
    background-size:200% 200%;
  }
  60%{
    background-position:150% 150%;
  }
    100% {
      /*left:-40%;*/
      background-size:250% 100%;
      background-position:right;
    }
}

.gradient-animation {
    background-repeat: repeat;
    animation: gradient-animation 50s linear infinite;
}

@keyframes gradient-animation {
    0% {
        background-position: 0% 0%;
    }
    100% {
        background-position: 100% 100%;
    }
}

.battlefield > div[style*="--selected-sleeve"].dropdown{
  border: 2px solid #56AFFB56; /* Green border */
  border-radius: 4px;
  box-shadow: 0px 0px 10px 5px rgba(200, 100, 140, 0.6), 0px 10px 40px 10px rgba(0, 0, 0, 0.5); /* Red and black box shadow */
  background-size:300px 500px;
  background-position:center;
  background-image: radial-gradient(circle,#f00, #270c5dd9,#ff10899e );
    animation: rotateGradient 5s cubic-bezier(0, 1.18, 0.55, -0.12) infinite;
    }

    @keyframes rotateGradient {
      0% { background-position: -50% -50%; }
      25%{background-position: -50% -10%;}
      50%{background-position:0% 20%}
      75%{background-position: 25% 0%;}
      90% {background-position:0% 0%;}
      100% { background-position: -50% -50%; }
    }

/*big card preview*/
div.d-none.d-md-inline-block{
  opacity:1;
  border: 2px solid #56AFFB56; /* Green border */
  border-radius: 4px;
  overflow:hidden;
  box-shadow: 0px 0px 10px 5px rgba(40, 200, 140, 0.5), 0px 10px 40px 10px rgba(0, 0, 0, 0.5); /* Red and black box shadow */
}

.library > * div.dropdown{
  transform: rotate(-5deg);
    background: linear-gradient(to left, #ff8a00, #e52e71, #8a2be2, #4b0082, #00ced1, #ff8a00);
     background-size: 600% 100%; /* Six colors, so 600% width */
     animation: gradientAnimation 10s ease infinite; /* Adjust animation duration as needed */
    }
    @keyframes gradientAnimation {
        0% { background-position: 0% 0%; }
        50% { background-position: 100% 50%; }
      100%{background-position:0% 0%;}
    }

.library > * div.dropdown{
  transform: rotate(-5deg);
    background: linear-gradient(to left, #ff8a00, #e52e71, #8a2be2, #4b0082, #00ced1, #ff8a00);
     background-size: 600% 100%; /* Six colors, so 600% width */
     animation: gradientAnimation 10s ease infinite; /* Adjust animation duration as needed */
    }
    @keyframes gradientAnimation {
        0% { background-position: 0% 0%; }
        50% { background-position: 100% 50%; }
      100%{background-position:0% 0%;}
    }

