style.css:
:root {
    --background-color: #121212; /* Dark background as seen in the image */
    --text-color: #ffffff;       /* White text color for contrast */
    --teal-color: #31b6bd;       /* Provided teal color */
    --font-family-base: 'Segoe UI', 'Roboto', sans-serif; /* Common fonts similar to the one in the image */
    --border-color: #2c2c2c;     /* Border color for elements */
}

/* Apply a dark theme to the body */
body {
    background-color: var(--background-color);
    color: var(--font-family-base);
    font-family: var(--font-family-base);
    margin: 0;
    padding: 0;
    line-height: 1.5;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
    text-rendering: optimizeLegibility;
}
