style.css:
[data-filter-name="Arcane Sheriff"] {
    position: relative;
}

[data-filter-name="Arcane Sheriff"]::after {
    content: "LEGENDARY";
    position: absolute;
    bottom: 105px; /* En ortanın biraz üstüne yerleştirme */
    left: 50%; /* Bloğun tam ortasına hizalama */
    transform: translateX(-50%); /* Yatayda hizalamayı düzeltme */
    font-size: 16px;
    font-weight: bold; /* Kalın yazı */
    color: #ffffff; /* Turuncu renk */
    background-color: #ff7f00; /* Yeşil renk */
    padding: 6px 10px;
    border-radius: 8px;
}
