style.css:
@-moz-document domain("app.tmetric.com") {
:root {
        --bg: #222222;
        --border: #3d3d3d;
        --font: #ffffff;
        --pageBg: #1d1d1d;
        --light-grey: #f6f7f8;
        --green: #29c770;
        --light-green: #39ff72;
        --red: #da373c;
        --light-red: #ff4040;
        --yellow: #f0b232;
        --purple: #9db3ff;
    }

    content *,
    nav .nav-item:not(.active) .nav-link:not(:hover),
    nav .nav-item.dropdown-item li:not(.active) a:not(:hover),
    nav .nav.user-menu *,
    .badge-secondary,
    ::selection,
    .btn-outline-secondary:not(:disabled):not(.disabled),
    .btn-secondary:not(:disabled):not(.disabled) {
        color: var(--font) !important;
    }

    content,
    footer,
    header,
    .navbar-default {
        background-color: var(--pageBg);
        box-shadow: none !important;
    }

    .logo-box {
        background-color: var(--border);
        margin: 0 !important;
        padding: .8125rem 1.375rem .8125rem 4rem !important;
    }

    button.navbar-toggler:not(:hover) {
        filter: brightness(50) saturate(100%);
    }

    .panel,
    .panel-default>.panel-heading,
    .dropdown-menu,
    .modal-content,
    .alert-warning,
    .editor-footer-actions button.btn-outline-secondary:not(:disabled):not(.disabled):hover {
        background-color: var(--bg) !important;
        border-color: var(--border);
        color: var(--font);
    }

    .modal-header,
    .modal-footer {
        border-color: var(--border);
    }

    .page-bar,
    .datepicker.dropdown-menu {
        background-color: var(--bg);
        border: 1px solid var(--border);
        color: var(--font);
    }

    .report-filter,
    .report-filter-btn {
        border-color: var(--border);
    }

    .panel-body .well,
    .card,
    .card-header,
    .alert-secondary {
        background-color: var(--border);
        border-color: var(--bg);
        border-radius: 0;
    }

    .timeline-bar.break {
        fill: var(--bg);
    }

    .timeline-tick-text,
    .timeline-tooltip-text {
        fill: var(--font);
    }

    .timeline-warning>label {
        opacity: 0.5;
    }

    .timer-table .timer-timeentry-editor,
    .form-group .list-group-item,
    .tag-items-list,
    .btn-timer-stop:disabled {
        background-color: var(--border) !important;
    }

    .form-control,
    .select2-container--bootstrap .select2-selection--single,
    .select2-container--bootstrap .select2-selection--multiple {
        border-color: var(--border);
    }

    .table-hover>tbody>tr:hover,
    .table>tbody>tr.active>td,
    .table>tbody>tr.active>th,
    .table>tbody>tr>td.active,
    .table>tbody>tr>th.active,
    .table>tfoot>tr.active>td,
    .table>tfoot>tr.active>th,
    .table>tfoot>tr>td.active,
    .table>tfoot>tr>th.active,
    .table>thead>tr.active>td,
    .table>thead>tr.active>th,
    .table>thead>tr>td.active,
    .table>thead>tr>th.active,
    .datepicker .datepicker-switch:hover,
    .datepicker .next:hover,
    .datepicker .prev:hover,
    .datepicker tfoot tr th:hover,
    .datepicker table tr td span:hover,
    .datepicker table tr td.day.focused,
    .datepicker table tr td.day:hover,
    .row-group-edit {
        background-color: var(--light-grey);
    }

    .filter-container .btn-group .dropdown-toggle:hover,
    .filter-container .btn-group .dropdown-toggle:active,
    .btn-secondary:hover,
    .btn-secondary:active,
    .input-group.disabled .input-group-append .input-group-text {
        background-color: var(--light-grey) !important;
    }

    .table-hover>tbody>tr:hover td>div>span:not(.badge-success):not(.badge-secondary),
    .table-hover>tbody>tr:hover td>small,
    .table-hover>tbody>tr:hover td>small>span>span,
    .table>tbody>tr.active>td span,
    .table>tbody>tr.active>th span,
    .table>tbody>tr>td.active span,
    .table>tbody>tr>th.active span,
    .table>tfoot>tr.active>td span,
    .table>tfoot>tr.active>th span,
    .table>tfoot>tr>td.active span,
    .table>tfoot>tr>th.active span,
    .table>thead>tr.active>td span,
    .table>thead>tr.active>th span,
    .table>thead>tr>td.active span,
    .table>thead>tr>th.active span,
    .table>tbody>tr,
    .table>tbody>tr:hover .btn-icon-add:not(:hover) i,
    .table>tbody>tr:hover .btn-icon:not(:hover) i,
    .table>tbody>tr:hover strong,
    .table>tbody>tr:hover>td,
    .table>thead>tr>th:hover:not([title="Team"]),
    .table>thead>tr>th:hover:not([title="Team"]) i,
    .table>tbody>tr:hover>td>span:not(.badge-secondary),
    .table>tbody>tr:hover>td>div>button>i,
    .table>tbody>tr:hover>td>span>span {
        color: var(--bg) !important;
    }

    .form-control,
    .btn-outline-secondary,
    .select2-container--bootstrap .select2-selection,
    calendar-integration button.dropdown-toggle:not(:hover) {
        background-color: var(--border) !important;
        border-color: var(--bg) !important;
        outline: none !important;
        box-shadow: none !important;
    }

    .btn-secondary,
    .custom-checkbox .custom-control-label::after,
    .select2-container--bootstrap .select2-selection--multiple .select2-selection__choice,
    .badge-secondary {
        background-color: var(--bg) !important;
        border-color: var(--bg) !important;
    }

    .table td,
    .table th {
        border-color: var(--bg);
    }

    .btn-timer-continue:not(:disabled) {
        background-color: var(--green);
    }

    .timer-buttons .btn-timer-continue:not(:disabled):hover,
    .timer-buttons .btn-timer-continue:not(:disabled):active {
        background-color: var(--light-green) !important;
    }

    .btn-timer-stop:not(:disabled),
    .badge-danger,
    .btn-danger,
    .btn-danger-light:hover {
        background-color: var(--red);
    }

    .timer-buttons .btn-timer-stop:not(:disabled):hover,
    .timer-buttons .btn-timer-stop:not(:disabled):active {
        background-color: var(--light-red) !important;
    }

    .badge-success {
        background-color: var(--green);
    }

    .badge-warning {
        background-color: var(--yellow);
        color: var(--bg) !important;
    }

    .alert-info {
        background-color: var(--purple);
    }

    .btn-icon-success:hover,
    .btn-icon-success:active,
    .btn-icon-add:hover,
    .btn-icon-add:active,
    .btn-icon-success.btn-icon-start:not(:disabled):hover,
    .btn-icon-success.btn-icon-start:not(:disabled):active,
    .btn-icon-success.btn-task-complete:not(:disabled):hover,
    .btn-icon-success.btn-task-complete:not(:disabled):active,
    button:not(:disabled)[title="Add time entry"]:hover,
    button:not(:disabled)[title="Start timer"]:hover,
    button:not(:disabled)[tooltip="Start timer"]:hover {
        background-color: var(--green);
    }

    tr .btn-icon-danger:hover,
    tr .btn-icon-danger:active,
    .btn-icon-danger.btn-icon-start:not(:disabled):hover,
    .btn-icon-danger.btn-icon-start:not(:disabled):active {
        background-color: var(--red);
    }

    span[title="Stop timer"] .btn-icon-danger:hover i,
    span[title="Stop timer"] .btn-icon-danger:active i,
    span[title="Continue task"] .btn-icon-success:hover i,
    span[title="Continue task"] .btn-icon-success:active i,
    .btn-icon-danger.btn-icon-start:not(:disabled):hover i,
    .btn-icon-danger.btn-icon-start:not(:disabled):active i,
    .row-group-edit span.badge-pill,
    .editor-footer-actions button.btn-outline-secondary:not(:disabled):not(.disabled):hover {
        color: var(--font) !important;
    }

    .btn-icon-danger:hover,
    .btn-icon-danger:active,
    .btn-danger-light {
        background-color: var(--light-red);
    }

    .btn-icon-danger:not(:hover) i,
    .btn-icon-danger:not(:active) i {
        color: var(--red) !important;
    }

    .btn-icon-success:not(:hover) i,
    .btn-icon-success:not(:active),
    button:not(:disabled)[tooltip="Start timer"]:not(:hover) i,
    button:not(:disabled)[tooltip="Start timer"]:not(:active),
    button:not(:disabled):not(.btn-timer-start)[title="Start timer"]:not(:hover) i,
    button:not(:disabled):not(.btn-timer-start)[title="Start timer"]:not(:active) {
        color: var(--green) !important;
    }

    .user-selector>.dropdown>.dropdown-toggle:hover>span,
    .user-selector>.dropdown>.dropdown-menu li a:hover span,
    .user-selector>.dropdown>.dropdown-menu li.selected a span,
    .page-title .dropdown>button:focus span,
    .page-title .dropdown>button:hover span,
    .page-title .dropdown>button:focus i,
    .page-title .dropdown>button:hover i,
    .bs-datepicker-container .bs-datepicker-body,
    .btn-icon-default:not(:disabled):hover i,
    .btn-icon-default:not(:disabled):focus i,
    .navbar-vertical .nav-item .nav-link:hover a span,
    nav .nav-item:not(.active) .nav-link:hover,
    nav .user-menu .login-box:not(.active) .login-link:hover *,
    .balance-panel-value a,
    .balance-panel-value span,
    .task-item .tag,
    .btn-sort span,
    .btn-sort span strong,
    .btn-sort i,
    .dropdown-item:hover:not(.nav-item):not(.dropright):not(.subdropdown),
    .dropdown-item:hover i,
    .item-selector-dropdown li>a:hover *,
    .user-avatar:hover .user-name,
    .task-item:hover .description span,
    .filter-container .btn-group .dropdown-toggle:hover span,
    .filter-container .btn-group .dropdown-toggle:active span,
    .filter-container .btn-group .dropdown-toggle:hover i,
    .filter-container .btn-group .dropdown-toggle:active i,
    .table-hover tr[team-dashboard-time-item]:hover td:not(.timer-status) *,
    .ui-datepicker table tr td a.ui-state-active:not(:hover),
    .ui-datepicker table tr td a:not(.ui-state-active):hover,
    .ui-datepicker-list-item:not(.ui-datepicker-selected-item):not(:hover) div,
    .ui-datepicker .ui-corner-all:hover span,
    .user-selector .button:hover i,
    .btn.btn-secondary:hover,
    .btn.btn-secondary:active,
    .btn.btn-secondary:hover span,
    .btn.btn-secondary:active span,
    .row-group-edit span,
    .row-group-edit small,
    .row-group-edit i,
    .row-group-edit td,
    .row-group-edit>td>span>a,
    .tt-dropdown-menu>div *,
    .dropdown-item:hover:not(.nav-item):not(.dropright):not(.subdropdown) span,
    div.active:not(.nav-item):not(.dropright):not(.subdropdown),
    .btn-outline-primary-light.active,
    .btn-outline-primary-light:hover,
    .input-group-append .input-group-text,
    tag-item div:hover .tag-name span,
    tag-item div:hover span>i,
    tag-item div:hover div.dropdown>button>i,
    .avatar-selector a,
    calendar-integration button.dropdown-toggle:hover>i,
    .workspaces-item.dropdown-item:hover a {
        color: var(--bg) !important;
    }

    .card-panel,
    .list-group-item,
    .user-box,
    .nav-box {
        background-color: var(--bg);
    }
}
