style.css:
@-moz-document domain("clockify.me") {
/*      Sidebar     */
    /*--- Background ---*/
    .cl-navbar {
        background: #000;
        box-shadow: 0 5px 10px rgba(0, 0, 0, .1);
        box-shadow: 0px 0px;
    }
    .cl-layout-aside {
        background-color: #000;
    }
    /*--- Itens background ---*/
    .cl-offcanvas-collapse {
        background: #000;
    }
    /* Focused */
    .cl-sidebar-container .cl-nav-item.cl-active .cl-nav-link,
    .cl-sidebar-container .cl-nav-link.cl-active {
        background: #161717;
    }
    .cl-navbar-light .cl-navbar-nav .cl-active > .cl-nav-link,
    .cl-navbar-light .cl-navbar-nav .cl-nav-link.cl-active,
    .cl-navbar-light .cl-navbar-nav .cl-nav-link.cl-show,
    .cl-navbar-light .cl-navbar-nav .cl-show > .cl-nav-link {
        color: rgba(255, 255, 255, .7);
    }
    .cl-sidebar-wrapper {
        box-shadow: 0px 0px;
        /* sidebar left inset shadow*/
    }

    /*  sidebar links color */
    .cl-navbar-light .cl-navbar-nav .cl-nav-link,
    .cl-navbar-light .cl-navbar-nav .cl-nav-link:focus,
    .cl-navbar-light .cl-navbar-nav .cl-nav-link:hover {
        color: #acacac;
    }

    /*--- Didebar icons ---*/
    .cl-nav-icon-wrapper img {
        filter: invert(1);
    }

    /* sidebar arrow */
    .cl-dropdown-notification-desktop:before,
    .cl-navbar-expand-lg .cl-navbar-nav .cl-dropdown-menu:before {
        filter: invert(1);
    }

    /* page background */
    body {
        background: #1a1a1b
    }

    /* cards background */
    .cl-card {
        background-color: #000;
        border: 1px solid #252829;
        border: none;
        /* removing border */
        border-radius: 0px 0px 15px 15px;
    }
    div.cl-card.cl-border-bottom.cl-mb-4 {
        border-radius: 15px!important;
        border-bottom: 1px solid #000000!important;
    }

    table-info.cl-card.cl-border-bottom-0.cl-mb-0,
    div.cl-card.cl-reset-component-row-last-child {
        border-top-left-radius: 15px;
        border-top-right-radius: 15px;
    }

    entry-group .cl-card {
        border-radius: 15px;
    }

    div.cl-card.cl-border-bottom-0.cl-mb-0.cl-d-print-none {
        border-radius: 15px;
    }

    parent-tracker-entry,
    time-tracker-entry {
        border-bottom: 1px solid #c6d2d9;
        border-bottom: none;
    }

    .cl-card-header {
        background-color: #262829;
        border-bottom: 1px solid #262829;
        border-radius: 15px 15px 0 0!important;
        /* border a little bit more round */
    }

    /* card lines separator */
    .cl-reset-component-row-last-child .cl-component-row:last-child {
        border-bottom: none;
        border-top: 1px solid #0b0c0c;
    }

    /*      Top Right Buttons       */
    .cl-btn-outline-light:not(:disabled):not(.cl-disabled).cl-active,
    .cl-btn-outline-light:not(:disabled):not(.cl-disabled):active,
    .cl-show > .cl-btn-outline-light.cl-dropdown-toggle {
        border-color: #353535;
        background-color: #353535;
        color: #c5c5c5;
    }

    .cl-btn-outline-light.cl-active:active,
    .cl-btn-outline-light.cl-active:hover,
    .cl-btn-outline-light:active,
    .cl-btn-outline-light:hover,
    .cl-show > .cl-btn-outline-light.cl-dropdown-toggle:active,
    .cl-show > .cl-btn-outline-light.cl-dropdown-toggle:hover {
        border-color: #000;
        color: #dbdbdb;
        background: #000;
    }

    .cl-form-control {
        background-color: #151515
    }
    td .cl-main-horizontal-chart-container--label {
        color: #d9d9d9
    }
    span {
        color: #d9d9d9;
    }
    .cl-main-horizontal-chart-container--progress--bar {
        background: #0c0c0c;
    }
    div.cl-ml-2 {
        color: #d9d9d9;
    }
    .cl-bg-white {
        background-color: #000000!important;
    }
    .cl-text-dark,
    .cl-cut-text {
        color: #d9d9d9!important;
    }
    .cl-btn-outline-light,
    .cl-btn-outline-light.cl-active,
    .cl-show > .cl-btn-outline-light.cl-dropdown-toggle {
        border-color: #000;
        color: #a9a9a9;
        background: #000;
        border-radius: 5px
    }

    button.cl-btn.cl-btn-fixed-40.cl-btn-outline-light.cl-d-print-none.ng-star-inserted {
        border-left-color: #111;
    }

    .cl-datepicker-wrapper .cl-datepicker-fake-input span {
        border-top-right-radius: 0px;
        border-bottom-right-radius: 0px;
    }
    .cl-collapse-nav-button {
        background: black
    }
    .cl-redesign .cl-h1,
    .cl-redesign h1,
    .modal-dialog .cl-h1,
    .modal-dialog h1 {
        color: #a9a9a9!important;
    }
    .cl-redesign [type=text],
    .modal-dialog [type=text] {
        color: #a9a9a9;
    }
    .cl-redesign .cl-input-time-picker-sum,
    .modal-dialog .cl-input-time-picker-sum {
        color: #a9a9a9!important;
    }
    .cl-btn-outline-light.cl-dropdown-toggle:disabled,
    .cl-btn-outline-light.cl-dropdown-toggle:disabled:hover,
    input[type=number]:disabled,
    input[type=number]:disabled:hover,
    input[type=text]:disabled,
    input[type=text]:disabled:hover,
    textarea:disabled,
    textarea:disabled:hover {
        color: #a9a9a9!important;
    }
    .cl-badge-same-entries,
    .cl-badge-tag {
        background: #070808;
    }
    .cl-dropdown-menu {
        background-color: #000;
    }
    .cl-dropdown-item {
        color: #b5b5b5;
        background-color: black;
    }
    .cl-dropdown-item.cl-active,
    .cl-dropdown-item:active {
        color: #c7c5c5;
        background-color: #101010;
    }
    .cl-border-light {
        border-color: #191a1b!important;
    }
    .cl-border-bottom {
        border-bottom: 1px solid #252829!important;
    }
    .cl-dropdown-divider {
        border-color: #202223;
    }
    .cl-dropdown-ws .cl-dropdown-ws-footer {
        background: #000;
    }
    .cl-sidebar-container .cl-nav-item:before {
        background: #121213;
    }

    /*      calendar    */
    div.daterangepicker.ltr.auto-apply.show-ranges.show-calendar.opensleft {
        background-color: #000;
    }

    div.drp-calendar.left.calendar-table {
        background-color: #000;
    }

    .daterangepicker .calendar-table td,
    .daterangepicker .calendar-table th {
        border-radius: 4px;
        border: 1px solid #1c1c1c;
        background-color: #000;
        color: white;
    }
    .daterangepicker .calendar-table {
        border: 1px solid #000;
        border-radius: 4px;
        background-color: #000;
    }

    .daterangepicker {
        border: none;
    }

    dashboard-charts .cl-card,
    dashboard-latest-activity .cl-card {
        border-radius: 15px!important;
    }

    .cl-border-bottom {
        border-bottom: 1px solid #252829!important;
        border-bottom: 0px!important;
        border-top: 1px solid #25282929!important;
    }

    .cl-redesign parent-tracker-entry .cl-input-description,
    .cl-redesign parent-tracker-entry .cl-input-time-picker,
    .cl-redesign parent-tracker-entry .cl-input-time-picker-sum,
    .cl-redesign parent-tracker-entry .cl-input-timetracker-main,
    .cl-redesign time-tracker-entry .cl-input-description,
    .cl-redesign time-tracker-entry .cl-input-time-picker,
    .cl-redesign time-tracker-entry .cl-input-time-picker-sum,
    .cl-redesign time-tracker-entry .cl-input-timetracker-main,
    .cl-redesign time-tracker-recorder .cl-input-description,
    .cl-redesign time-tracker-recorder .cl-input-time-picker,
    .cl-redesign time-tracker-recorder .cl-input-time-picker-sum,
    .cl-redesign time-tracker-recorder .cl-input-timetracker-main {
        border: 1px solid transparent;
        border-radius: 6px;
    }

    /*      time entry      */
    time-tracker-entry.cl-collapse {
        background: #0d0d0d!important;
        /* border-bottom-left-radius: 15px; */
        border: none!important;
    }

    .cl-component-row-colored,
    table tbody tr.cl-component-row-colored:nth-child(2n),
    tbody tr.cl-component-row-colored:nth-child(2n) {
        background: #0d0d0d!important;
    }

    div.cl-component-row.cl-flex-column.cl-flex-lg-row.cl-hover-shadow {
        color: #bfbfbf;
    }

    .cl-redesign [type=text],
    .modal-dialog [type=text] {
        color: #a9a9a9;
        background-color: #131313;
        border-color: #000;
    }

    .cl-custom-control-label:before {
        pointer-events: none;
        background-color: #131313;
        border: 1px solid #c6d2d9;
    }

    .cl-dropdown-item.cl-open,
    .cl-dropdown-item:focus,
    .cl-dropdown-item:hover {
        background: #0d0d0d;
    }

    /*  projects table  */
    table.cl-projects-table.cl-table.cl-table-bordered.cl-remove-cell-borders.cl-text-gray-6 {
        background-color: #0d0d0d!important;
    }

    thead {
        background: #000;
    }

    tbody,
    tfoot,
    thead {
        border: 1px solid #f1f1f1;
        border: none!important;
        background-color: #111!important;
    }

    tr.ng-star-inserted {
        background-color: #111!important;
    }

    .cl-table.cl-remove-cell-borders.cl-table-bordered td,
    .cl-table.cl-remove-cell-borders.cl-table-bordered th {
        border-right: none;
        border-bottom: none;
        border-left: none;
        border-top: none;
    }


    .cl-table-bordered,
    .cl-table-bordered td,
    .cl-table-bordered th {
        border: 1px solid #c6d2d9;
        border: 0px;
    }

    .cl-btn-outline-primary {
        background: #202020;
    }

    .cl-nav-tabs .cl-nav-link.cl-active {
        background: #000;
        border-radius: 6px 6px 0px 0px;
    }

    .cl-scroll-tabs-wrapper .cl-scroll-tabs .cl-nav-item .cl-nav-link {
        border: 1px solid #000;
    }

    .cl-scroll-tabs-wrapper .cl-scroll-tabs .cl-nav-item .cl-nav-link.cl-active,
    .cl-scroll-tabs-wrapper .cl-scroll-tabs .cl-nav-item .cl-nav-link:active {
        border-bottom-color: #000;
    }

    .cl-nav-tabs .cl-nav-link {
        background: #444;
        border: 0px!important;
        color: #bbb;
        border-radius: 6px 6px 0px 0px;
    }
}
