style.css:
@-moz-document domain("fibery.io") {
html, body, span, div, td, th, input, textarea, select, button, h1, h2, h3, h4, h5, h6, p, ant-input {
    font-optical-sizing: auto;
    font-style: normal;
    font-stretch: extra-condensed;
  }

  em {
    font-stretch: extra-condensed;
    font-variation-settings:
      'wght' 300,
      'wdth' 100,
      'slnt' 8.00; 
  }

  html, body, li, p, li p, .ProseMirror li p {
    letter-spacing: .3px;
  }

  .layout.sidebar_toggle_button {
    transform: translateX(calc(-100% - 0px));
    height:40px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    border-top-right-radius: 0;
    border-bottom-right-radius: 0;
  }

  .layout.sidebar_toggle_button > .sidebar_toggle_button {
    height: 40px;
  }
}
