style.css:
@-moz-document url-prefix("https://app.demiplane.com/nexus/pathfinder2e/") {
/* Tables */
.builder-table-row {
    padding-top: 2px;
    padding-bottom: 2px;
    min-height: 36px;
  	margin: 4px 0px;
}

.builder-left-column .builder-table-row {
    padding-top: 0px;
    padding-bottom: 0px;
  	margin-bottom: 0px;
}

/* Top panels */
.center-content-main-container {
    gap: 8px;
}

.center-content-block--bottom {
    gap: 8px;
}

/* Bottom panels */
.sheet-bottom-container .sheet-box-component-content {
    padding: 5px;
    gap: 2px;
}

@media (min-width: 1024px) and (min-height: 1080px) {

    .grid-block:not(.details-tab-content) > .sheet-bottom-container .tab-content-container {
        max-height: calc(100vh - 640px);
        height: calc(100vh - 640px);
    }
    
    .grid-block:not(.details-tab-content) > .sheet-bottom-container .sheet-box-component-content {
        height: calc(100vh - 642px);
    }
    
    .sheet-box-component.skills-box-component {
        max-height: calc(100vh - 640px);
    }
    
    .sheet-box-component.actions-box-component {
        max-height: calc(100vh - 640px);
    }
    
    div#details-tab-content {
        max-height: calc(100vh - 640px);
    }
    
    .sheet-collapsed .grid-block:not(.details-tab-content) > .sheet-bottom-container .tab-content-container {
        height: calc(100vh - 336px);
    }
    
}

/* Skill box */
.skills-box-component .button-component {
    margin-bottom: 0px;
}

.skills-box-component .grid-table-row {
    margin: 2px 0px;
  }

.skills-box-component .stacked-title__description {
  	display: none;  
}


/* Side panel */
.drawer-container .builder-table-row {
	padding: 2px 8px;
}

/* CHARACTER BUILDER */
/* General */
.builder .element-block__clamp-block {
    top: -10px;
  	margin-top: 0px;
}

.select-list-container .builder-table-row, 
.options-list-container .builder-table-row,
.builder-right-column .builder-table-row{
    margin: 0px;
  	height: 50px
}

/* Central column */
.builder-middle-column > div:first-child > div:first-child {
  height: 56px;
}

.class-container .builder-table-row__open .builder-table-row{
  height: 40px;
  margin: 3px 0px;
}

/* Right column */
.builder .right-content-container {
  padding: 12px;
}

.builder-right-column > div:first-child > div:first-child {
    height: 56px;
}

.builder-right-column:after {
  top: -10px;
  height: 16px;
}

.builder-right-column .grid-table-row {
    margin: 6px 0px;
}

.builder-right-column .skill-increase-table .builder-table-row{
  height: 40px;
}
}
