style.css:
/* Estilo Personalizado para o xCloud */

/* Altera a cor de fundo da página para uma imagem */
body {
    background-image: url('https://i.ibb.co/YXrQ7Kk/car-gif.gif') !important;
    background-size: cover !important;  /* Faz a imagem cobrir toda a tela */
    background-repeat: no-repeat !important;  /* Evita a repetição da imagem */
    background-attachment: fixed !important;  /* Faz a imagem de fundo ficar fixa ao rolar a página */
    color: #ffffff !important;  /* Cor do texto branca */
}

/* Altera o estilo dos cabeçalhos */
h1, h2, h3, h4, h5, h6 {
    font-family: 'Arial', sans-serif !important;
    color: #61dafb !important;  /* Cor azul clara */
}

/* Altera o estilo dos links */
a {
    color: #61dafb !important;
}

a:hover {
    color: #21a1f1 !important;
}
