style.css:
/* Changer la couleur de fond de la page */
body {
    background-color: #181818 !important;
}

/* Modifier la couleur de fond des tableaux */
div[data-test-id="board"], div[data-test-id="pin"], div[data-test-id="pinWrapper"] {
    background-color: #282828 !important;
    border: 1px solid #444 !important;
}

/* Modifier la couleur du texte */
body, div, span, a, p, h1, h2, h3, h4, h5, h6 {
    color: #FFFFFF !important;
}

/* Personnaliser la barre de recherche */
input[type="text"], input[type="search"], div[data-test-id="searchBox"] {
    background-color: #333 !important;
    color: #FFF !important;
    border: 1px solid #444 !important;
    border-radius: 5px !important;
}

/* Modifier les boutons */
button, a[data-test-id="button"] {
    background-color: #444 !important;
    color: #FFF !important;
    border: none !important;
    border-radius: 3px !important;
}

/* Modifier les éléments de navigation */
header, nav, div[data-test-id="header"], div[data-test-id="dashboard"], div[data-test-id="header"] > div, div[data-test-id="boardHeader"] {
    background-color: #282828 !important;
    border-bottom: 1px solid #444 !important;
}

/* Personnaliser les menus déroulants */
div[data-test-id="dropdown"] {
    background-color: #333 !important;
    color: #FFF !important;
    border: 1px solid #444 !important;
}

/* Personnaliser les cartes de pin */
div[data-test-id="pinCard"] {
    background-color: #282828 !important;
    border: 1px solid #444 !important;
}

/* Personnaliser les textes des pins */
div[data-test-id="pinCard"] div, div[data-test-id="pinCard"] span, div[data-test-id="pinCard"] a {
    color: #FFFFFF !important;
}

/* Personnaliser les descriptions des pins */
div[data-test-id="pinDescription"] {
    background-color: #333 !important;
    color: #FFF !important;
    border: 1px solid #444 !important;
    border-radius: 5px !important;
    padding: 10px !important;
}

/* Personnaliser les commentaires des pins */
div[data-test-id="comment"], div[data-test-id="comments"], div[data-test-id="commentHeader"], div[data-test-id="commentSection"], div[data-test-id="commentList"], div[data-test-id="commentContainer"] {
    background-color: #333 !important;
    color: #FFF !important;
    border: 1px solid #444 !important;
    border-radius: 5px !important;
    padding: 10px !important;
}

/* Modifier les notifications */
div[data-test-id="notification"] {
    background-color: #282828 !important;
    color: #FFF !important;
    border-bottom: 1px solid #444 !important;
}

/* Personnaliser les sections de profil */
div[data-test-id="profileSection"] {
    background-color: #181818 !important;
    color: #FFF !important;
    border-top: 1px solid #444 !important;
}
