/* Dark Mode for whitescreen.online */
/* by ShaneX */

html {
    background-color: #000000;
    color: #696969;
}

body {
    background-color: #000000;
    color: #696969;
}

h1, h2 {
    color: #696969;
}

.site-name a {
    color: #f0f0f0;
}

.feedback {
    color: #222222;
}

.lang-dropbtn {
    background-color: #000000;
    color: #222222;
}

.main-color {
    box-shadow: 0 8px 30px 0 #3366ff;
}

button {
    color: #424242;
    background-color: #000000;
    border: 1px solid #424242;
    -webkit-border-radius: 3px;
    -moz-border-radius: 3px;
    border-radius: 3px;
    margin: 0px 5px 0px 5px;
}

.download-btn {
    margin-top: 15px;
}

#open-on-all-screens {
    margin-bottom: 25px;
}

select, input, label {
    color: #222222;
    border-bottom: 1px solid #222222 !important;
}

.gallery a, .gallery-right-color, .color-right-text {
    color: #424242;
}

@media screen and (max-width: 1024px) {
    .gallery-left {
        padding-top: 0px;
        padding-bottom: 0px;
        padding-right: 10px;
        width: 174px;
    }
    .color-block {
        padding: 0px 0px 5px 0px;
    }
}

.color-block-selected {
    background-color: #1e2035;
  }
}
