style.css:
@-moz-document domain("chess.com") {
body, .board, .sidebar, .content, .header, .footer {
    background-color: #fff !important;
    color: #000 !important;
  }
  
  /* Override specific elements */
  .navbar, .navbar a, .menu, .menu a {
    background-color: #fff !important;
    color: #000 !important;
  }
  
  /* Ensure text is visible */
  .text-light, .text-muted {
    color: #000 !important;
  }
  
  /* Handle board colors */
  .board-b72b1, .board-grey, .board-sand {
    background-color: #f0d9b5 !important; /* light square */
  }
  
  .board-b72b1 .board-dark, .board-grey .board-dark, .board-sand .board-dark {
    background-color: #b58863 !important; /* dark square */
  }
  
  /* Override buttons and other UI elements */
  .button, .input, .dropdown, .modal {
    background-color: #fff !important;
    color: #000 !important;
    border-color: #ccc !important;
  }
  
  /* Adjust links */
  a {
    color: #0066cc !important;
  }
}
