style.css:
html {
  background-color: rgb(206,215, 158);
}

::-webkit-scrollbar {
  background-color: transparent;
  width:6px;
}
	
::-webkit-scrollbar-thumb  {
	background-color: rgb(206,215, 158);
}

body {
    filter: contrast(0.4) sepia(1) hue-rotate(32deg);
}

* {
  font-family: Green Screen, Zelda DX TT BRK, BPdotsSquares, Press Start,  Bittypix Monospace, LLPixel !important; 
  font-size: 18px !important;
  border-radius: 0 !important;
}

img {
  image-rendering: pixelated
}

html::after {
    content: " ";
    position: fixed;
    z-index: 10000009;
    top: 0;
	left: 0;
    width: 100%;
    height: 100%;
	pointer-events: none;
    box-sizing: border-box;
    box-shadow: inset 0px 0px 100px 60px #00000088;
}
