style.css:
/* Aplicar uma aparência pixelada aos textos e imagens */
body, p, h1, h2, h3, h4, h5, h6, a, span, div, td, button, input, textarea, img {
    font-family: monospace !important;
    image-rendering: pixelated !important;
}

/* Aumentar o contraste e saturação para um visual mais retro */
body, img, video {
    filter: contrast(150%) saturate(120%);
}

/* Estilizar links */
a {
    color: #00ff00 !important; /* Verde neon */
    text-decoration: none !important;
}

a:hover {
    color: #ff0000 !important; /* Vermelho */
    text-decoration: underline !important;
}

/* Estilizar botões */
button, input[type="button"], input[type="submit"] {
    background-color: #000 !important;
    color: #00ff00 !important; /* Verde neon */
    border: 2px solid #00ff00 !important; /* Verde neon */
    cursor: pointer !important;
}

button:hover, input[type="button"]:hover, input[type="submit"]:hover {
    background-color: #00ff00 !important; /* Verde neon */
    color: #000 !important;
}

/* Estilizar todas as células de tabela */
td {
    background-color: #000000 !important;
    color: #00ff00 !important;
    border: 1px solid #00ff00 !important;
}

/* Estilizar links dentro das células de tabela */
td a {
    color: #00ff00 !important;
    display: block !important;
    line-height: 3.8em !important;
}
