style.css:
/* Universal Dark Theme */
@namespace url(http://www.w3.org/1999/xhtml);

/* Global background and text colors */
* {
  background-color: #121212 !important;
  color: #e0e0e0 !important;
  border-color: #333 !important;
}

/* Adjust specific elements */
a, a:visited {
  color: #bb86fc !important;
}

a:hover {
  color: #3700b3 !important;
}

input, textarea, select, button {
  background-color: #333 !important;
  color: #e0e0e0 !important;
  border: 1px solid #444 !important;
}

img, video {
  opacity: 0.9 !important;
}

table, th, td {
  background-color: #222 !important;
  border-color: #333 !important;
}

hr {
  border-color: #333 !important;
}

/* Code blocks */
pre, code {
  background-color: #2e2e2e !important;
  color: #dcdcdc !important;
}

/* Specific overrides for common websites */
body, html {
  background-color: #121212 !important;
  color: #e0e0e0 !important;
}

/* Override styles for certain elements to ensure readability */
::placeholder {
  color: #777 !important;
}

button, input[type="submit"], input[type="button"], input[type="reset"] {
  background-color: #333 !important;
  color: #e0e0e0 !important;
  border: 1px solid #444 !important;
}

header, footer, nav, section, article {
  background-color: #1e1e1e !important;
}

/* Specific overrides for readability and usability */
form, fieldset, legend {
  background-color: #1e1e1e !important;
  color: #e0e0e0 !important;
  border: 1px solid #333 !important;
}

label, caption {
  color: #bbbbbb !important;
}

/* Ensuring buttons and inputs are visible */
button, input, textarea, select {
  background-color: #333 !important;
  color: #e0e0e0 !important;
  border: 1px solid #444 !important;
}

/* Handling highlights and selections */
::selection {
  background-color: #bb86fc;
  color: #121212;
}

::-moz-selection {
  background-color: #bb86fc;
  color: #121212;
}

/* Additional tweaks for common readability issues */
blockquote {
  background-color: #1e1e1e !important;
  color: #e0e0e0 !important;
  border-left: 5px solid #444 !important;
}

abbr[title] {
  border-bottom: 1px dotted #444;
  cursor: help;
}

/* Override button hover */
button:hover, input[type="submit"]:hover, input[type="button"]:hover, input[type="reset"]:hover {
  background-color: #444 !important;
  color: #ffffff !important;
}
