style.css:
@-moz-document domain("mail.google.com"), domain("inbox.google.com") {
/* Đặt nền gradient mờ và hình nền cây */
    .aeN {
        background: linear-gradient(rgba(0, 0, 0, 0.5), rgba(0, 0, 0, 0.5)), url('https://images.pexels.com/photos/33045/lion-wild-africa-african.jpg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1') no-repeat bottom;
        background-size: cover;
        color: white;
    }

    /* Chỉnh sửa nút Compose */
    .T-I.T-I-KE.L3 {
        background-color: white !important;
        color: black !important;
        border-radius: 4px !important;
        font-size: 16px !important;
        padding: 10px 20px !important;
    }

    /* Đặt màu chữ cho menu */
    .aim, .nU, .TK {
        color: white !important;
    }

    /* Đặt màu nền và chữ cho các mục menu khi hover */
    .aim:hover, .nU:hover, .TK:hover {
        background-color: rgba(255, 255, 255, 0.1) !important;
    }
}
