/* fixing the game tiles to be the old ones | GAMES PAGE CARD FIX */
.thumb-container img {
    width: 150px;
    height: 150px;
    overflow: hidden;
    transition: opacity .5s ease;
    opacity: 1;
}
.hover-game-tile {
    height: 100%;
    max-height: 300px;
    width: 150px;
    margin-right: 14px;
    transition: all .0s cubic-bezier(0,0,0,1);
}

/* game tile shadows */ 
.light-theme .hover-game-tile.visible-tile:hover .featured-game-container {
    box-shadow: 0 1px 6px 0 rgb(25 25 25 / 0.75);
}
.dark-theme .game-card-container, .light-theme .game-card-container {
    box-shadow: 0 1px 4px 0 rgb(25 25 25 / 0.60);
}
