    box-shadow: 0 1px 6px 0 rgb(25 25 25 / 0.75);
}
.hover-game-tile .featured-game-container .featured-game-icon-container .brief-game-icon {
    height: 150px;
    width: 150px;
    border-top-left-radius: 3px !important;
    border-top-right-radius: 3px !important;
    transition: all .0s cubic-bezier(0,0,0,1) !important;
}
.hover-game-tile .featured-game-container{transition: all 0s cubic-bezier(0,0,0,1) !important;}
.game-card-container {
    margin-left: 1px;
}
