style.css:
/* Apply dark theme to the entire page */
body {
    background-color: #000 !important;
    color: #fff !important;
    font-family: 'Creepster', cursive, Arial, sans-serif !important;
    margin: 0 !important;
    padding: 0 !important;
}

/* Add creepy eyes background image */
body::before {
    content: "";
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: url('https://i.imgur.com/rB0DPfQ.png') no-repeat center center;
    background-size: contain;
    opacity: 0.8; /* Adjust opacity for the desired effect */
    z-index: -1; /* Ensure the image stays in the background */
}

/* Style headers and text */
h1, h2, h3, h4, h5, h6, p, a, label, input, button {
    color: #fff !important;
}

/* Style forms */
input, button {
    background-color: #222 !important;
    color: #fff !important;
    border: 1px solid #444 !important;
    border-radius: 5px !important;
    padding: 0.5em !important;
}

/* Add animation to creepy eyes */
body::before {
    animation: blink 3s infinite;
}

/* Keyframes for blinking effect */
@keyframes blink {
    0%, 20%, 40%, 60%, 80%, 100% {
        opacity: 0.8;
    }
    10%, 30%, 50%, 70%, 90% {
        opacity: 0;
    }
}
