style.css:
@-moz-document domain("www.turkhackteam.org") {
.p-pageWrapper {
        position: relative;
        display: flex;
        flex-direction: column;
        min-height: 100vh;
        background: #0A0A0A !important;
    }

    .block-tabHeader {
        padding: 0;
        margin: 0;
        
        font-size: 14px;
        color: #0A0A0A !important;
        background: #636363;
        border-bottom: 1px solid #232323;
        
        font-weight: bold;
    }

    .xgtForumIstatistik .xgtForumIstatistik-tabHeader.block-tabHeader {
        background: #636363 !important;
        color: #0A0A0A !important;
        border-bottom: none;
    }

    .p-nav {
        color: #ffffff !important;
        background: #0A0A0A !important;
    }

    .block-container {
        color: #bfbfbf;
        background: #0A0A0A !important;
        border-width: 1px;
        border-style: solid;
        border-top-color: #232323;
        border-right-color: #232323;
        border-bottom-color: #161616;
        border-left-color: #232323;
        border-radius: 3px;
    }

    .p-body-sidebar .block .block-minorHeader,
    .p-body-sideNavContent .block .block-minorHeader {
        font-size: 16px;
        color: #bfbfbf;
        font-weight: 600;
        background: #636363 !important;
        border-bottom: 2px solid #232323;
    }

    .block-container {
        color: #bfbfbf;
        background: #0A0A0A !important;
        border-width: 1px;
        border-style: solid;
        border-top-color: #232323;
        border-right-color: #232323;
        border-bottom-color: #161616;
        border-left-color: #232323;
        border-radius: 3px;
    }

    html {
        font-family: 'Verdana', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    }

    .p-header-content {
        padding: 12px 0;
        display: flex;
        flex-wrap: wrap;
        justify-content: space-between;
        align-items: center;
        max-width: 100%;
        background-color: #0A0A0A !important;
    }

    .xgtForumIstatistik ._xgtIstatistik-satir._xgtIstatistik_miniHeader {
        background: linear-gradient(0deg, #0A0A0A, #353535);
        border-bottom: none;
    }


    .xgtForumIstatistik ._xgtIstatistik-satir:nth-child(1n) {
        background: #0A0A0A !important;
    }

    a {
        color: #B5B5B5;
        text-decoration: none;
        font-weight: bold;
    }
    
    .block--messages article.message .message-cell.message-cell--main {
    border: 1px solid #1c1c1c;
    background: #0A0A0A !important;
}
    
   .message-cell.message-cell--user, .message-cell.message-cell--action {
    position: relative;
    background: #0D0D0D !important;
    border-right: 1px solid #232323;
    min-width: 0;
}
    
    .message-body {
    margin: 14px 0;
    font-family: 'Poppins', 'Segoe UI', 'Helvetica Neue', Helvetica, Roboto, Oxygen, Ubuntu, Cantarell, 'Fira Sans', 'Droid Sans', sans-serif;
    font-size: 14px;
}
    
    @media (min-width: 1001px) {
    .p-header {
        padding-top: 15px;
        padding-bottom: 15px;
        background-color: #0A0A0A !important;
        background-image: none !important;
    }
}
    
    .menu-content {
    margin: 0;
    padding: 0;
    list-style: none;
    text-align: left;
    font-size: 13px;
    color: #bfbfbf;
    background: #0A0A0A;
    border-radius: 3px;
    border-top: 3px solid #131313;
}
    
    .structItemContainer .structItemContainer-group:not(.structItemContainer-group--sticky) .structItem:nth-child(even):not(.is-mod-selected) {
    background-color: #0A0A0A;
}
    
    .structItemContainer-group--sticky {
    background: #0A0A0A !important;
}
    
    .memberHeader-main {
    display: flex;
    flex-direction: column;
    justify-content: flex-end;
    border-bottom: 1px solid #232323;
    border-radius: 2px 2px 0 0;
}
    
   .avatar.avatar--m {
    width: 145px;
    height: 150px;
    font-size: 58px;
}
    
    .avatar {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    vertical-align: top;
    overflow: hidden;
    border-radius: 0 !important;
}
    
    .message-avatar-wrapper {
    position: relative;
    display: inline-block;
    vertical-align: bottom;
    margin-bottom: .5em;
    border: 3px solid #A0A0A0;
    border-radius: 5px;
}
    
    .xb-searchWrapper .xb-search>input {
    padding-left: 37px;
    color: #f9f9f9;
    background: #282828;
    border-width: 0px;
    border-style: solid;
    border-bottom: 1px solid #0A0A0A;
    width: 200px;
    height: 30px;
}
    
    .input {
    background: #0a0a0a !important;
    color: #353535 !important;
    border-radius: 2% !important;
}
       
    
    .memberTooltip-header {
    display: table;
    table-layout: fixed;
    width: 100%;
    padding: 12px;
    border-bottom: 1px solid #232323;
}
    
    .message-name {
    font-weight: 600;
    font-size: inherit;
    text-align: center;
    margin: 6px;
    padding: 0px;
    border-radius: 2px;
    background: linear-gradient(to bottom, #333333, #000000);
}
    
    .memberTooltip-name {
    margin: 0;
    margin-top: -.15em;
    padding-left: 22px;
    font-weight: 400;
    line-height: 1.28;
    font-size: 22px;
    color: #bfbfbf;
}
    
    .memberTooltip-banners, .memberTooltip-blurb {
    margin-top: .25em;
    margin-left: .25em;
}
    
    .memberTooltip-blurb {
    font-size: 13px;
    
}
    
    .memberTooltip-info, .memberTooltip-actions {
    padding: 12px;
    background-color: #0A0A0A !important;
}
    
    .fr-box.fr-basic {
    background: #0A0A0A !important;
    border: solid 1px #232323;
    border-color: var(--input-border-heavy) var(--input-border-light) var(--input-border-light) var(--input-border-heavy);
    border-radius: 3px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    -moz-background-clip: padding;
    -webkit-background-clip: padding-box;
    background-clip: padding-box;
}
    
    .message-userTitle {
    font-size: 12px;
    font-weight: normal;
    text-align: center;
    margin: 0;
    color: #fff;
    border-radius: 7%;
}
    
    .message-userExtras dl {
    background-color: #0D0D0D;
    padding: 6px 12px;
    border: #0D0D0D;
    border-radius: 3px;
    margin-bottom: 3px;
}
    
    .block--messages .message-inner:last-of-type .message-cell:first-child {
    border-radius: 7px;
    border: 1px solid #fff;
    background: #0D0D0D !important;
}
    
    .block--messages .message-inner:last-of-type .message-cell:last-child {
    border-radius: 5px;
}
    
    
    
    .message-userExtras {
    margin-top: 3px;
    font-size: 12px;
}
    
    .bbCodeBlock {
    display: flow-root;
    margin: .5em 0;
    background: #0A0A0A;
    border: 1px solid #161616;
}
    
    .bbCodeBlock-title {
    padding: 12px 14px;
    font-size: 13px;
    color: #BB0000;
    background: #0A0A0A;
}
    
    .memberTooltip-blurbContainer {
            padding-left: 20px; 
}
    
    .block-container .structItemContainer-group .is-unread .structItem-title a {
    font-weight: 600;
    color: #fff;
}
    
    .message-responseRow {
    margin-top: -1px;
    background: #0A0A0A;
    border: 1px solid #232323;
    padding: 12px;
}
}
