style.css:
/* Dark background */
body {
  background-color: #222222 !important;
  color: #ffffff !important;
}

/* White text */
h1, h2, h3, p {
  color: #ffffff !important;
}

/* Royal blue buttons */
button {
  background-color: #4169e1 !important;
  color: #ffffff !important;
  border: none;
  padding: 10px 20px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  margin: 4px 2px;
  cursor: pointer;
}
