style.css:
/* Đặt nền trang thành màu hồng và chữ màu trắng */
body {
    background-color: #FFC0CB; /* Màu hồng nhạt */
    color: #FFFFFF; /* Chữ màu trắng */
    font-family: 'Arial', sans-serif;
    margin: 0;
    padding: 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 100vh;
}

/* Thiết kế header với màu xanh mát */
header {
    background-color: #008080; /* Màu xanh mát */
    padding: 20px;
    width: 100%;
    text-align: center;
}

/* Thiết kế chữ tiêu đề */
h1 {
    margin: 0;
    font-size: 2.5rem;
}

/* Thiết kế nội dung chính */
main {
    text-align: center;
}

/* Thiết kế nút bấm */
button {
    background-color: #008080; /* Nút màu xanh mát */
    color: #FFFFFF; /* Chữ màu trắng */
    border: none;
    padding: 10px 20px;
    font-size: 1rem;
    cursor: pointer;
    transition: background-color 0.3s;
}

/* Hiệu ứng hover cho nút bấm */
button:hover {
    background-color: #005757; /* Màu xanh mát đậm hơn khi hover */
}

/* Thiết kế đoạn văn bản */
p {
    font-size: 1.2rem;
}
