style.css:
@-moz-document domain("rationalskepticism.org") {
html {
        font-size: 0.8vw;
        box-sizing: border-box;
        background-color: #000
    }

    /* === GLOBAL === */
    *,
    *::after,
    *::before {
        margin: 0;
        padding: 0;
        box-sizing: inherit;
        transition: all 222ms ease;
        outline: none;
        text-decoration: none !important
    }
    *:focus {
        outline: none
    }
    body,
    body#phpbb,
    p,
    blockqutoe,
    ul,
    ol,
    li,
    dd label {
        color: #ccc
    }
    body,
    body#phpbb {
        background: none #000;
        font-size: 100%;
        line-height: 1.46
    }

    a,
    a:visited,
    a:active,
    a.postlink,
    a.postlink:visited,
    a.postlink:active,
    a:hover,
    a.postlink:hover {
        background-color: inherit;
        border: 0;
        text-decoration: none
    }
    a,
    a:visited,
    a:active,
    a.postlink,
    a.postlink:visited,
    a.postlink:active {
        color: #8fc
    }
    a:hover,
    a.postlink:hover {
        color: #59c
    }
    .wrap,
    .headerwrap,
    .footerwrap {
        width: 96%;
        max-width: 1500px;
        background-color: #000;
        margin: 0 auto
    }
    .headerwrap,
    .footerwrap,
    .panel-container .panel li.row,
    ul.cplist {
        border-color: #555
    }
    .page-header,
    .page-footer {
        max-width: 1400px
    }
    .bg1,
    .panel {
        background-color: #181818
    }
    .bg2,
    blockquote,
    blockquote blockquote blockquote,
    blockquote blockquote blockquote blockquote blockquote {
        background-color: #222
    }
    .bg3,
    blockquote blockquote,
    blockquote blockquote blockquote blockquote {
        background-color: #333
    }
    .content h2,
    .panel h2,
    h3#review,
    .panel,
    .panel h3,
    #postingbox h3,
    .panel-container .panel li.header dt,
    .panel-container .panel li.header dd {
        color: #bbb;
        border-color: #555
    }
    blockquote {
        border-color: #666;
        padding: 0 1rem 1rem 1rem
    }
    hr {
        border: 0 !important;
        width: 100%;
        height: 1px;
        background-color: #555
    }

    /* === HEADER === */
    .site-description {
        overflow: hidden
    }
    .site_logo {
        background-image: none
    }
    .site_logo:after {
        content: "RATIONAL SKEPTICISM";
        color: #fff;
        font-family: Georgia, Serif;
        font-size: 1.2rem;
        font-weight: 200
    }
    .lightsearch ul.linklist .light-large {
        font-size: 1.3rem
    }
    .lightsearch a:link,
    .lightsearch a:visited,
    .lightsearch a:active {
        color: #aaa
    }
    .lightsearch a:hover {
        color: #59c
    }
    .navbar-top {
        background-color: inherit
    }
    .lightheader,
    .lightnav {
        border-color: #555
    }
    ul.linklist > li.breadcrumbs,
    .navbar-top .icon {
        font-size: 1rem;
        font-weight: 400
    }
    li.breadcrumbs .crumb a,
    .navbar-top .icon {
        color: #999
    }
    .navbar-top .icon:hover,
    li.breadcrumbs .crumb a:hover {
        color: #59c
    }
    h2.topic-title a:link,
    h2.topic-title a:visited,
    h2.topic-title a:active,
    th a,
    h2.solo,
    h2.ucp-title,
    h2.searchresults-title,
    .page-body h2 {
        color: #fff
    }
    a.header-avatar,
    a.header-avatar img.avatar {
        width: 2rem;
        height: 2rem
    }
    a.header-avatar {
        border-radius: 1rem;
        overflow: hidden
    }
    .dropdown .dropdown-contents {
        background: #222;
        border-color: #555
    }
    .dropdown-contents li a {
        font-size: 0.88rem;
        line-height: 1.08
    }
    .nav-main a[title="Notifications"],
    .nav-main a[title="Private messages"] {
        position: relative;
        top: -0.25rem
    }
    .dropdown .pointer,
    .dropdown .pointer-inner {
        border-color: #555 transparent
    }
    .dropdown li.separator {
        border-color: #222
    }
    .dropdown-extended .header {
        background-color: #222
    }
    .dropdown-extended .header,
    .dropdown-extended .footer {
        color: #ccc;
        border-color: #555;
    }
    .dropdown-extended ul li:hover {
        background-color: #333;
        color: #ddd
    }
    .dropdown li {
        font-size: 0.88rem;
        line-height: 1.2
    }
    .dropdown li,
    .dropdown li li,
    .dropdown-extended ul li,
    .jumpbox .dropdown li {
        border-color: #555
    }
    .dropdown-extended .pointer-inner,
    .jumpbox .dropdown .pointer-inner {
        border-color: #555 transparent
    }
    .dropdown-extended a.mark_read {
        color: #fff;
        background-color: #b79721;
        border-color: #b79721
    }
    .dropdown-extended a.mark_read:hover {
        color: #000;
        background-color: #4eb721;
        border-color: #4eb721
    }
    a.jumpbox-cat-link,
    a.jumpbox-sub-link {
        background-color: #222
    }
    a.jumpbox-cat-link:hover,
    a.jumpbox-sub-link:hover {
        background-color: #000
    }

    .notification_list p.notification-time {
        color: #888
    }
    ul#nav-main.rightside.nav-main.linklist.light-nav-list {
        position: relative;
        top: -1rem
    }

    /* === FOOTER === */
    .nav-footer i {
        font-size: 1rem
    }
    div.copyright span.footer-copyright {
        height: 1px;
        font-size: 0;
        color: #000;
        background: #000;
        overflow: hidden
    }
    div.copyright p:last-of-type:after {
        content: "\0052\0061\0074\0053\006b\0065\0070\0032\0034\003a\0020\0064\0061\0072\006b\0020\0073\0074\0079\006c\0065\0020\0062\0079\0020\0054\0048\0057\004f\0054\0048";
        height: 2rem;
        color: #aaa;
        font-size: 0.8rem;
        font-family: Georgia, Serif;
        line-height: 2;
        display: block;
        margin: 1rem auto
    }

    /* === TOPIC LIST === */
    .forumbg,
    .forabg,
    ul.topiclist,
    ul.forums {
        background-color: #181818
    }
    .forumbg,
    .forabg,
    ul.topiclist .row,
    ul.forums .row,
    ul.topiclist dd,
    li.row:hover dd {
        border-color: #555
    }
    ul.topiclist .list-inner a:link,
    ul.topiclist .list-inner a:visited,
    ul.topiclist .list-inner a:active,
    a.lastsubject:link,
    a.lastsubject:visited,
    a.lastsubject:active {
        color: #fff
    }
    ul.topiclist .list-inner a:hover,
    a.lastsubject:hover {
        color: #59c
    }
    ul.topiclist .row,
    ul.topiclist .row strong,
    ul.topiclist li {
        color: #bbb
    }
    ul.topiclist .row:hover {
        background-color: #343f34
    }
    .icon.icon-lightgray,
    a:hover .icon.icon-lightgray {
        color: #59c
    }
    .row:hover {
        filter: brightness(150%)
    }

    /* === POSTBODY === */
    .post {
        border: 0
    }
    .post,
    .post:target,
    .post:target .content,
    .postbody,
    .postbody .content {
        color: #ccc
    }
    .post h3 a,
    .post h3 a.first-unread,
    .post:target h3 a {
        color: #ccc
    }
    .postbody h3 a:hover,
    .post h3 a:hover,
    .post:target h3 a:hover {
        color: #59c
    }

    /* === AUTHOR === */
    .postprofile {
        font-size: 0.8rem;
        padding: 0 0 0 0.35rem;
        border-color: #555
    }
    .postprofile .avatar {
        width: 100%;
        height: auto
    }
    .postprofile .avatar img {
        margin-bottom: 0.75rem
    }
    .postprofile,
    .postprofile a:link,
    .postprofile a:visited,
    .postprofile a:active {
        color: #999
    }
    .postprofile strong {
        color: #ccc
    }
    cite span.responsive-hide {
        color: #888;
        font-size: 0.8rem
    }
    .postprofile a.username:hover {
        filter: brightness(130%)
    }

    /* === TABS === */
    .tabs .tab > a {
        top: 1px;
        background: #222;
        border-color: #555;
        border-bottom: 0;
        border-bottom-color: transparent
    }
    .tabs .tab > a {
        color: #ccc
    }
    .tabs .tab > a:hover {
        color: #eee;
        background-color: #444
    }
    .tabs .activetab > a,
    .tabs .activetab > a:hover {
        color: #ccc;
        background: #333;
        border-color: #555;
        box-shadow: 0 0 transparent
    }

    /* === BUTTONS & BOXES === */
    .button .icon {
        color: #999;
        background: #000
    }
    a.button-icon-only:hover i.icon {
        border-color: transparent !important
    }
    .button,
    input.button1,
    input.button2,
    .button-secondary,
    .pagination .button,
    a.button:link,
    a.button:visited,
    a.button:active {
        color: #999;
        stroke: #999;
        border: 1px solid #666;
        background: #111
    }
    .button:hover,
    input.button1:hover,
    input.button2:hover,
    input.button1:focus,
    input.button2:focus,
    .button-secondary:hover,
    a.button:hover,
    .pagination .button:hover,
    .pagination li.active span,
    .pagination li a:hover,
    .pagination li a:hover .icon,
    .pagination .dropdown-visible a.dropdown-trigger,
    .pagination .dropdown-container:hover a.dropdown-trigger {
        color: #fff;
        stroke: #fff;
        border: 1px solid #999;
        background-color: #333
    }
    .pagination .dropdown-visible a.dropdown-trigger,
    .pagination .dropdown-container:hover a.dropdown-trigger {
        border: 0
    }
    .pagination li.ellipsis span {
        color: #999
    }
    .search-header,
    .search-box .inputbox,
    .search-box .inputbox:hover,
    .search-box .inputbox:focus {
        border-color: #555
    }
    .search-box .inputbox {
        padding-bottom: 8px
    }
    input {
        position: relative;
        top: 1px;
        font-size: 0.76rem;
        font-family: monospace, monospace;
        line-height: 1.14;
        padding: 4px 2px
    }
    input.button1,
    input.button2 {
        font-size: 0.88rem;
        padding: 0 0.3rem
    }
    .button:focus .icon, 
    .button:hover .icon {
        color: #ccc
    }
    .inputbox,
    .message-box,
    .message-box .inputbox {
        color: #bbb;
        background-color: #000
    }
    .message-box .inputbox {
        padding: 0.24rem 0.5rem
    }
    input[type="text"],
    input[type="file"].inputbox {
        color: #bbb;
        background: #111;
        font-family: sans-serif;
        padding: 0.1rem 0.2rem;
        border-color: #555
    }
    input[type="text"]:hover,
    input[type="text"]:focus,
    input[type="file"].inputbox:hover,
    input[type="file"].inputbox:focus {
        color: #fff;
        background: #222;
        border-color: #999
    }
    #qr_postform .panel {
        border-radius: 0.2rem
    }
    #qr_postform input[name="subject"],
    #postingbox input[name="subject"] {
        color: inherit;
        background: inherit;
        font-family: inherit;
        font-size: 1.12rem;
        border: 0;
        outline: 0 none
    }
    fieldset dl:hover dt label {
        color: #ccc
    }
    fieldset.fields1 dd {
        margin-left: 3rem
    }

    input[type="checkbox"] {
        position: relative;
        top: -0.15rem;
        width: 0.85rem;
        height: 0.85rem;
        border: 2px solid #666
    }
    input[type="checkbox"]:checked {
        border-color: #ccc
    }
    select {
        color: #999;
        background: #000;
        font-size: 0.8rem;
        padding: 0.1rem 0.2rem;
        border-color: #555;
        border-radius: 3px
    }
    select:hover {
        color: #eee;
        background: #222;
        border-color: #888
    }
    select option:hover {
        color: #fff;
        background: #000
    }

    /* === UCP === */
    .navigation a,
    .navigation .active-subsection a {
        color: #ccc;
        background: #000;
        border-color: #555
    }
    .navigation a:hover,
    .navigation .active-subsection a:hover {
        color: #59c;
        background: #333;
        border: 1px solid #333
    }
    .panel-container .panel,
    .panel-container .panel .inner {
        background-color: #000
    }
    table.table1 tbody tr,
    table.table1 tbody td {
        border-color: #555
    }
    table.table1 td {
        color: #bbb
    }
    .panel-container table.table1 thead th {
        color: #eee;
        border-bottom-color: #555
    }
    .cp-mini {
        display: none
    }
    .pmlist li.pm_message_reported_colour, 
    .pm_message_reported_colour {
        border-left-color: #79270a !important
    }
    .pmlist li.pm_replied_colour, 
    .pm_replied_colour {
        border-left-color: #12640b !important
    }
    .pmlist li.pm_read, 
    .pm_read {
        border-left-color: #888 !important
    }

    /* === MISC === */
    .postbody img {
        max-width: 97%
    }
    a[style="color: #804080;"] {
        color: #b7b !important
    }
    .notice,
    .signature,
    html body#phpbb.notouch.section-ucp.ltr.hasjs div#wrap.wrap div#page-body.page-body div#tabs.tabs ul li.tab a[href="./ucp.php?i=280"] {
        display: none
    }
    .page-body p:first-of-type strong {
        color: #888
    }
    label {
        color: #bbb
    }
    p.post-notice {
        background-color: #803
    }
    .post-notice strong {
        color: #bbb !important
    }
    .username {
        font-weight: 600
    }
    dl.details dd,
    dl.details dt {
        font-size: 0.88em
    }
    dl.details dd {
        color: #ccc
    }
    h3#review span.right-box {
        font-size: 70%;
        margin-right: 1rem
    }
    dl.details dt {
        color: #aaa
    }

    @media screen and (max-width: 900px) {
        html {
            font-size: 1.3vw;
        }
        body, 
        .content,
        .post,
        .postbody, 
        .postprofile dd {
            line-height: 1.5
        }
        .postprofile {
            width: 25%
        }
        .postbody {
            width: 75%
        }
    }
    @media screen and (max-width: 732px) {
        dd.profile-joined,
        dd.profile-name, 
        dd.profile-residence,
        dd.profile-contact {
            display: none
        }
    }
    @media (max-width: 700px) {
        .logo {
            display: block
        }
        .site_logo {
            width: 60% !important
        }
        .postprofile {
            width: 150px;
        }
        div.postbody {
            width: 100%
        }
        div.postbody > * {
    
            }
    }
}
