style.css:
@-moz-document url-prefix("https://character.ai") {
.dark {
    --background: #e7d6d6;
    --hero-transparent: rgb(0 0 0 / 61%);
    --foreground: #857878;
    --card: #827a7a;
    --card-foreground: var(--G50);
    --card-alt: #fff;
    --card-alt-foreground: #363636;
    --popover: #646568;
    --popover-foreground: #f8fafc;
    --primary: #958989;
    --primary-foreground: var(--G950);
    --secondary: #e0dada;
    --secondary-foreground: #cacfd3;
    --muted: var(--G800);
    --muted-foreground: var(--G400);
    --accent: #484850;
    --accent-foreground: #f8fafc;
    --destructive: #7f1d1d;
    --destructive-foreground: #f8fafc;
    --error: var(--Error);
    --error-foreground: #f8fafc;
    --blue: var(--Blue);
    --border: #1e293b;
    --input: var(--G700);
    --ring: #1d4ed8;
    --border-outline: var(--G750);
    --border-divider: var(--G800);
    --scrim: #0000003c;
    --scrim-0: hsla(0, 0%, 97%, 0);
    --scrim-4: hsla(0, 0%, 97%, .04);
    --scrim-8: hsla(0, 0%, 97%, .08);
    --scrim-12: hsla(0, 0%, 97%, .12);
    --surface-base: #525255;
    --surface-elevation-1: var(--G850);
    --surface-elevation-2: var(--G800);
    --surface-elevation-3: var(--G750);
    --brand-bg: #505058;
    --icon-inverted: var(--G800);
    --icon-secondary: var(--G400);
    --icon-secondary-inverted: var(--G600);
    --icon-primary: var(--G200);
    --transparency-4: #f8f8f80f;
    --transparency-8: #f8f8f81e;
    --transparency-16: #f8f8f83c
}

:root {
    --G0: #f0e4e4;
    --G50: #cebbbb;
    --G100: #f4f4f5;
    --G150: #ececee;
    --G200: #7b7b81;
    --G250: #cfada5;
    --G300: #b58d8b;
    --G400: #5d5959;
    --G500: #8b7b7b;
    --G600: #493e3e;
    --G700: #606071;
    --G750: #fee;
    --G800: #6d5a58;
    --G850: #202024;
    --G900: #28282b;
    --G950: #d3b5b5;
    --Blue: #536dc6;
    --Error: #cc3434;
    --scrim: #f8f8f880;
    --scrim-0: rgba(19, 22, 22, 0);
    --scrim-4: rgba(19, 22, 22, .04);
    --scrim-8: rgba(19, 22, 22, .08);
    --scrim-12: rgba(19, 22, 22, .12);
    --spacing-0: 0px;
    --spacing-xxs: 4px;
    --spacing-xs: 8px;
    --spacing-s: 12px;
    --spacing-m: 16px;
    --spacing-l: 20px;
    --spacing-xl: 24px;
    --placeholder: var(--G500);
    --hero-transparent: hsla(0, 0%, 100%, .73);
    --foreground: var(--G800);
    --card: #fff;
    --card-foreground: var(--G850);
    --card-alt: #fff;
    --popover: var(--G200);
    --popover-foreground: var(--G800);
    --primary: var(--G850);
    --primary-foreground: var(--G50);
    --secondary: var(--G250);
    --secondary-foreground: #0f172a;
    --muted: var(--G400);
    --muted-foreground: var(--G600);
    --accent: var(--G300);
    --accent-foreground: var(--G800);
    --destructive: #ef4444;
    --destructive-foreground: #f8fafc;
    --error: var(--Error);
    --error-foreground: #f8fafc;
    --blue: var(--Blue);
    --border: #e2e8f0;
    --input: #e2e8f0;
    --ring: #2563eb;
    --radius: 2rem;
    --border-outline: var(--G250);
    --border-divider: var(--G200);
    --surface-base: var(--G50);
    --surface-elevation-1: var(--G150);
    --surface-elevation-2: var(--G200);
    --surface-elevation-3: var(--G250);
    --brand-bg: var(--G150);
    --icon-inverted: var(--G200);
    --icon-secondary: var(--G600);
    --icon-secondary-inverted: var(--G400);
    --icon-primary: var(--G800);
    --transparency-4: #1313160f;
    --transparency-8: #1313161e;
    --transparency-16: #1313163c;
    --plus-gradient: linear-gradient(94.45deg, #81f1fe 1.11%, #f3ff9f 16.21%, #ffa2ba 34.92%, #d6d6d6 60.53%);
    --plus-gold: #eeb82c
}

.prose {
    --tw-prose-body: #374151;
    --tw-prose-headings: #111827;
    --tw-prose-lead: #4b5563;
    --tw-prose-links: #111827;
    --tw-prose-bold: #111827;
    --tw-prose-counters: #6b7280;
    --tw-prose-bullets: #d1d5db;
    --tw-prose-hr: #e5e7eb;
    --tw-prose-quotes: #111827;
    --tw-prose-quote-borders: #e5e7eb;
    --tw-prose-captions: #6b7280;
    --tw-prose-kbd: #111827;
    --tw-prose-kbd-shadows: 17 24 39;
    --tw-prose-code: #111827;
    --tw-prose-pre-code: #e5e7eb;
    --tw-prose-pre-bg: #1f2937;
    --tw-prose-th-borders: #d1d5db;
    --tw-prose-td-borders: #e5e7eb;
    --tw-prose-invert-body: #e7e9ec;
    --tw-prose-invert-headings: #fff;
    --tw-prose-invert-lead: #9ca3af;
    --tw-prose-invert-links: #fff;
    --tw-prose-invert-bold: #fff;
    --tw-prose-invert-counters: #9ca3af;
    --tw-prose-invert-bullets: #4b5563;
    --tw-prose-invert-hr: #374151;
    --tw-prose-invert-quotes: #f3f4f6;
    --tw-prose-invert-quote-borders: #374151;
    --tw-prose-invert-captions: #9ca3af;
    --tw-prose-invert-kbd: #fff;
    --tw-prose-invert-kbd-shadows: 255 255 255;
    --tw-prose-invert-code: #fff;
    --tw-prose-invert-pre-code: #d1d5db;
    --tw-prose-invert-pre-bg: rgba(0,0,0,.5);
    --tw-prose-invert-th-borders: #4b5563;
    --tw-prose-invert-td-borders: #374151;
    font-size: 1rem;
    line-height: 1.75
}
}
