style.css:
@-moz-document domain("roblox.com") {
/* Hide ads and related elements */
    .ad, .ad *, .img_ad, .ad-annotations, #google_image_div, #aw0, #AdvertisingLeaderboard {
        display: none !important;
    }

    /* Change header background */
    #header {
        background-color: rgb(48, 97, 227) !important;
    }

    /* Change notification icon background */
    .navigation .notification-icon, .rbx-highlight {
        background: rgb(48, 97, 227) !important;
    }

    /* Change chat header background */
    #chat-header {
        background: rgb(48, 97, 227) !important;
    }

    /* Change specific container backgrounds */
    .slide-item-container-left, .profile-avatar-right {
        background: rgb(48, 97, 227) !important;
    }

    /* Adjust tab styles */
    .rbx-tabs-horizontal .rbx-tab.active .rbx-tab-heading,
    .rbx-tabs-horizontal .rbx-tab-heading:hover,
    .rbx-tabs-horizontal .rbx-tab-heading:focus,
    .rbx-tabs-horizontal .rbx-tab-heading:active {
        box-shadow: 0 -4px 0 .6rem rgba(48, 97, 227, 0.8) inset !important;
    }

    /* Change forum table headers and related styles */
    .forum-table-header, body .forum-table-header th, body .forum-table-footer,
    #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th,
    #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: rgb(48, 97, 227) !important;
    }

    /* Change button and form element backgrounds */
    .rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more,
    #AjaxCommentsContainer > div > div.comment-form > form > button,
    .rbx-vip-server-create, .btn-secondary-xs {
        background-color: rgb(48, 97, 227) !important;
        border-color: rgb(48, 97, 227) !important;
    }

    /* Hover styles for buttons */
    .rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover,
    #AjaxCommentsContainer > div > div.comment-form > form > button:hover,
    .rbx-vip-server-create:hover, .btn-secondary-xs:hover {
        background: rgb(48, 97, 227) !important;
        border-color: rgb(48, 97, 227) !important;
    }

    /* Adjust search input and button borders */
    #navbar-search-input, #navbar-search-btn {
        border: 0 solid rgb(48, 97, 227) !important;
    }

    /* Set body and content backgrounds to transparent */
    body, .content {
        background: transparent !important;
    }

    /* Set Facebook root properties */
    #fb-root {
        width: 100% !important;
        height: 100% !important;
        position: fixed !important;
        background-image: url("https://i.postimg.cc/Tw7BB1Wh/new-classic-sonic-mania-wallpaper-by-cosmicblaster97-dbk52s1-4.png") !important;
        background-size: cover !important;
        background-attachment: fixed !important;
    }

    /* Set specific party and friend chat header backgrounds */
    #party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off {
        background: linear-gradient(to bottom, rgb(48, 97, 227), #000000) !important;
    }

    /* Hide specific columns */
    #ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3),
    #Body > table > tbody > tr > td:nth-child(4) {
        display: none !important;
    }

    /* Adjust games page column width */
    #GamesPageLeftColumn {
        width: auto !important;
        margin-right: 10px !important;
        margin-left: 10px !important;
    }

    /* Adjust games list container styles */
    #GamesListsContainer > div {
        background-color: transparent !important;
        width: 100% !important;
    }

    /* Change navigation background color */
    #navigation {
        background-color: rgb(48, 97, 227) !important; /* Left side navigation */
    }

    /* Change primary large buttons background color */
    .rbx-btn-primary-lg {
        background-color: rgb(48, 97, 227) !important;
    }

    /* Hover and focus styles for primary large buttons */
    .rbx-btn-primary-lg:hover, .rbx-btn-primary-lg:focus {
        background-color: rgb(48, 97, 227) !important;
    }
}
