style.css:
@-moz-document domain("youtube.com") {
ytd-app {
background: url('https://images.rawpixel.com/image_800/cHJpdmF0ZS9sci9pbWFnZXMvd2Vic2l0ZS8yMDIyLTA1L3JtMjE4YmF0Y2g5LW15bnQtMzguanBn.jpg');
}
#contents .button-container * {
	color: #ffffff!important;
}
ytd-rich-grid-media #dismissible, ytd-rich-shelf-renderer #dismissible {
	background: #1d1d1db8;
	min-height: 320px!important;
	max-height: unset!important;
	padding: 10px 10px 0px;
	border-radius: 10px;
}
.button-container.ytd-rich-shelf-renderer {
	background: none;
}
.yt-spec-button-shape-next {
	background: #000000;
}
.ytd-watch-flexy #primary {
	background: #0F0F0F;
	padding: 0px 10px;
}
#contentContainer {
	background: transparent!important;
}
#guide-content, #chips-wrapper, #search-icon-legacy {
	background: #000000cc!important;
}
}

@-moz-document url-prefix("https://www.youtube.com/watch") {
.ytd-two-column-browse-results-renderer {    
	background: #0F0F0F;
	padding: 0px 20px;
}
#items.ytd-watch-next-secondary-results-renderer {
	background: #1d1d1db8;
	padding: 10px;
	border-radius: 10px;
}
}
