style.css:
@-moz-document domain("youtube.com") {
ytd-app {
background: url('https://images.rawpixel.com/image_800/cHJpdmF0ZS9sci9pbWFnZXMvd2Vic2l0ZS8yMDIyLTA1L3JtMjE4YmF0Y2g5LW15bnQtMzguanBn.jpg');
}
#header.ytd-search {
	background: #000000cc!important;
  padding: 0px 20px;
  border-bottom-left-radius: 10px;
  border-bottom-right-radius: 10px;
}
#contents .button-container * {
	color: #ffffff!important;
}
ytd-rich-grid-media #dismissible, ytd-rich-shelf-renderer #dismissible {
	background: #1d1d1db8;
	min-height: 320px!important;
	max-height: unset!important;
	padding: 10px 10px 0px;
	border-radius: 10px;
}
.button-container.ytd-rich-shelf-renderer {
	background: none;
}
.yt-spec-button-shape-next {
	background: #000000;
}
.ytd-watch-flexy #primary {
	background: #0F0F0F;
	padding: 0px 10px;
}
#contentContainer {
	background: transparent!important;
}
#guide-content, #chips-wrapper, #search-icon-legacy {
	background: #000000cc!important;
}
#background.ytd-masthead {
  background: #000000cc;
}
ytd-two-column-search-results-renderer {
  background: #1d1d1db8!important;
  padding: 10px 20px;
  margin-top: 20px!important;
  border-radius: 10px;
}
[page-subtype="channels"] ytd-two-column-browse-results-renderer {
	background: #1d1d1db8;
	padding: 10px;
}
[page-subtype="channels"] .yt-spec-button-shape-next {
  background: #0404048c!important;
}
[page-subtype="channels"] #dismissible {
	background: initial;
  min-height: initial!important;
  max-height: initial!important;
  padding: initial;
  border-radius: initial;
}
[page-subtype="channels"] #chips-wrapper {
  background: initial!important;
}
[page-subtype="history"] ytd-two-column-browse-results-renderer {
	background: #1d1d1db8;
	padding: 10px;
}
[page-subtype="history"] .yt-spec-button-shape-next {
  background: #0404048c!important;
}
[page-subtype="history"] #dismissible {
	background: initial;
  min-height: initial!important;
  max-height: initial!important;
  padding: initial;
  border-radius: initial;
}
[page-subtype="history"] #chips-wrapper {
  background: initial!important;
}
[page-subtype="playlist"] #primary {
	background: #1d1d1db8;
	padding: 10px;
}
ytd-offline-item-section-renderer {
 	background: #1d1d1db8;
	padding: 10px; 
}


.ytd-watch-flexy .ytd-two-column-browse-results-renderer {    
	background: #0F0F0F;
	padding: 0px 20px;
}
.ytd-watch-flexy #primary {
  padding-left: 20px!important;
  border-top-left-radius: 10px;
}
.ytd-watch-flexy #secondary {
	background: #1d1d1db8;
	padding: 10px;
	border-top-right-radius: 10px;
}
.ytd-watch-flexy ytd-two-column-browse-results-renderer {
	background: #1d1d1db8;
	padding: 10px;
	border-bottom-left-radius: 10px;
  border-bottom-right-radius: 10px;
}
.ytd-watch-flexy ytd-rich-grid-media #dismissible, .ytd-watch-flexy ytd-rich-shelf-renderer #dismissible {
	background: initial;
  min-height: initial!important;
  max-height: initial!important;
  padding: initial;
  border-radius: initial;
}
.ytd-watch-flexy #chips-wrapper {
  background: initial!important;
}
.ytd-watch-flexy .yt-spec-button-shape-next {
  background: #0404048c!important;
}
}
