style.css:
body {
    /* Background color resembling Roblox's primary color */
    background-color: #E0E0E0;

    /* Background image with Roblox logo */
    background-image: url('https://i.pinimg.com/564x/16/d2/a9/16d2a952f0a124db9acf9bb589d291f8.jpg');
    
    /* Making sure the background covers the entire screen */
    background-size: cover;
    
    /* Ensuring the background is centered */
    background-position: center;
    
    /* Making the background static so it doesn't scroll with content */
    background-attachment: fixed;
    
    /* Apply the background to the entire HTML body */
    height: 100vh;
    margin: 0;
}

/* Optional: Adding a semi-transparent overlay to the background */
body::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.5); /* Adjust the opacity as needed */
    z-index: -1; /* Make sure the overlay stays behind other content */
}

/* Styling for text and elements to fit the theme */
h1, h2, h3, p {
    color: #333; /* Dark color for readability */
    font-family: 'Arial', sans-serif; /* Simple, clean font */
}

a {
    color: #0056b3; /* Complementary color for links */
    text-decoration: none; /* Remove underline from links */
}

a:hover {
    text-decoration: underline; /* Underline on hover for links */
}
